	/**
	 * Function which define which page is available for GTM
	 */
    function getScopeGtm(pageContextNs) {
		var generalScopeGTM = ['storefront', 'cms', 'page', 'category', 'search', 'product',
			'cart', 'shipping', 'payment', 'checkout', 'checkoutLogin', 'orderconfirmation', 'account', 'wishlist', 'storelocator', 'error'];

		return generalScopeGTM.indexOf(pageContextNs) >= 0;
    }

$(function() {
var env = 'desktop';
if(navigator.userAgent.match(/(android|iphone|ipad|blackberry|symbian|symbianos|symbos|netfront|model-orange|javaplatform|iemobile|windows phone|samsung|htc|opera mobile|opera mobi|opera mini|presto|huawei|blazer|bolt|doris|fennec|gobrowser|iris|maemo browser|mib|cldc|minimo|semc-browser|skyfire|teashark|teleca|uzard|uzardweb|meego|nokia|bb10|playbook)/gi)) {
if (((screen.width >= 480) && (screen.height >= 800)) || ((screen.width >= 800) && (screen.height >= 480)) || navigator.userAgent.match(/ipad/gi)) {
env = 'tablet';
} else {
env = 'mobile';
}
}
tc_vars['env_channel'] = env;
});

var tc_vars_user = [];

document.addEventListener('DOMContentLoaded', function() {
	if (getScopeGtm(pageContext.ns)) {
		tc_vars_user = "{"userLogged":"No","userSegment":"Everyone|Unregistered"}";

		tc_vars_user = Controller.convertToDataLayer(window.tc_vars_user);

		if (tc_vars_user) {
			if (tc_vars == null) {
				var tc_vars = [];
			}
			tc_vars = Object.assign(tc_vars, tc_vars_user);
		}
	}
})

$('body').on('click', '[data-gtm]', function(event) {
var gtm = JSON.parse(this.dataset.gtm);
(function(){
if(gtm.eventName === 'clickThumbnail') {
var CrossLookSlider = $('.js-CrossLook-slider');
var data = {};
data['productId'] = gtm.data.id;
data['productName'] = gtm.data.name;
data['productPrice'] = gtm.data.price;
if(CrossLookSlider.length){
tagmanager_event('clickPPCrossSellLook', data);
}
if(gtm.data.shoppingType === 'express-purchase'){
tagmanager_event('popinQuickBuyDisplay', data);
}
}
if(gtm.eventName === 'clickAddToCart') {
var size = $('.Swatch-sizeErp').find('option:selected').text().replace(/\s+/g, '');
gtm.data.size = size;
var uvc = $(event.target).parent().find("#uvc").val();
gtm.data.product_content_id = uvc;
}
tagmanager_event(gtm.eventName, gtm.data);
})();
});

window.tagmanager_event = function (event_name,parameters) {
try{
var tc_var_prefix = event_name + '_';
var tc_event_parameters={};
for (parameter in parameters){
tc_event_parameters[tc_var_prefix + parameter] = parameters[parameter];
}
tc_events_5(this, event_name, tc_event_parameters);
tc_events_11(this, event_name, tc_event_parameters);
//Pour l'espace de recette :
//console.log({event_name : event_name, event_parameters : tc_event_parameters});
} catch (e) {
console.log(e);
}
};

