body.script:
$(".scroll").click(function(event){
         event.preventDefault();
         //calculate destination place
         var dest=0;
         if($(this.hash).offset().top > $(document).height()-$(window).height()){
              dest=$(document).height()-$(window).height();
         }else{
              dest=$(this.hash).offset().top;
         }
         //go to destination
         $('html,body').animate({scrollTop:dest}, 1000,'swing');
     });
	 $('i').each(function(){
 		var classes = $(this).attr('class');
 		console.log(classes);
 		$(this).replaceWith("" + "")
 	});

$(function() {
	$('#main-nav-bar button').click(function(e) {
		e.preventDefault();
        console.log(e.target.classList[0]);
        if(e.target.classList[0] != 'dropdown' && e.target.classList[0] != 'dropdown-toggle'){
    		var href = $(this).attr('data-target');
    		if (href.indexOf('#') != -1) {
    			var id = href.replace('#', '');
    			var navIDClicked = 'subnav-' + id;
    			$('.subnav-container').each(function() {
    				var navID = $(this).attr('id');
    				if (navID == navIDClicked){
                        $(this).slideToggle();
                        if ($(this).parent().attr('aria-expanded') == 'true'){
                            $(this).parent().attr('aria-expanded', 'false');
                        }
                        else {$(this).parent().attr('aria-expanded', 'true')}
                    }
    				else{
                        $(this).slideUp();
                        $(this).parent().attr('aria-expanded', 'false');
                    }
    			});
    		} else {
    			if ($(this).prop("target") == '_blank') window.open(href, '_blank');
    			else window.location = href;
    		}
        }
	});
});
