div.script:
$(document).ready(function () {
    $("#0e49c76b-b76c-4e91-8064-ac7aa1a2d9d6").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 3000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 4 > 1 ? autoPlayValue : false;
    }
});

