function SetVideoWidthOnMobile(){
    var doc_width = $(document).width();
    var con_width = $(".contentboxinner").width();
    if(doc_width <= 767){
        video1 = $(".ac_main_content p > iframe") || [];
        video2 = $(".ac_main_content div > iframe") || [];
        video3 = $(".ac_main_content p > embed") || [];
        video4 = $(".ac_main_content div > embed") || [];
        video=$.merge(video1 , video2);
        video=$.merge(video , video3);
        video=$.merge(video , video4);
        for(var i=0;i<video.length;i++){
            if(!video[i].getAttribute("style")){
                var orig_width = video[i].width;
                var scale = con_width/orig_width;
                var new_height = video[i].height * scale;
                video[i].width= con_width;
                video[i].height= new_height;
            }
        }
    }
}

function SetImageOnMobile(){
    var doc_width = $(document).width();
    var con_width = $(".contentboxinner").width();
    if(doc_width <= 767){
        image1 = $(".ac_main_content p > img") || [];
        image2 = $(".ac_main_content div > img") || [];
        image=$.merge(image1 , image2);
        for(var i=0;icon_width){
                image[i].width= con_width;
                image[i].height= new_height;
            }
        }
    }
}

