function ShowLoginLine(){
    var a='MY ACCOUNTLOGOUT';
    var b='LOGINSIGN UP';
    $.ajax(
        {
            url:"/include/srn_lib/psr/html_fragments/check_login.php",
            dataType:"json",
            cache:false,
            dataType:"json",
            success:function(c){
                if(c.logged_in===true){
                    $("#header .search_bar").addClass("logged_in");
                    if($("#login_line").html()!==a){
                        $("#login_line").html(a)
                    }
                    if($("#m-login").html()!==a){
                        $("#m-login").html(a)
                    }
                }else{
                    $("#header .search_bar").removeClass("logged_in");
                    if($("#login_line").html()!==b){
                        $("#login_line").html(b)
                    }
                    if($("#m-login").html()!==b){
                        $("#m-login").html(b)
                    }
                }
            }
        }
    );
}

function checkIfOverLapped(el1, el2) {
    var rect1 = el1.getBoundingClientRect();
    var rect2 = el2.getBoundingClientRect();

    return !(
      rect1.top > rect2.bottom ||
      rect1.right < rect2.left ||
      rect1.bottom < rect2.top ||
      rect1.left > rect2.right
    );
}

function disableAdsClick(){
