link.script:
window.dataLayer = window.dataLayer || [];
function gtag(){dataLayer.push(arguments);}
gtag('js', new Date());

gtag('config', 'UA-50034135-33');

    $(function() {
    	CreativeTemplate.Init();
    });

	var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "ShowDistrictHome": false,
        "ShowSchoolList": false, // UPDATES IN SetTemplateProps METHOD
        "ShowTranslate": true,
		"ShowFullSiteButton": false,
        "ChannelBarIsSticky": false,

		// METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();

			this.MyStart();
			this.SchoolList();
			this.Translate();
            this.Header();
            this.Search();
            this.ChannelBar();
            if($(".hp").length) {
              this.Slideshow();
              this.CheckSlideshow();
            }

            this.Body();
            this.ModEvents();
            this.ArticleSlider();
            this.GlobalIcons();
            this.RsMenu();
            this.AppAccordion();
            this.Footer();
            this.MoveElements();
            this.SocialIcons();
            this.JsMediaQueries();
            this.ViewFullSite();
            this.DropShadows();

            $(window).load(function() { _this.WindowLoad(); });
            $(window).resize(function() { _this.WindowResize(); });
            $(window).scroll(function() { _this.WindowScroll(); });

            csGlobalJs.OpenInNewWindowWarning();
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;

            // SCHOOL LIST CHECK
        	if($(".sw-mystart-dropdown.schoollist").length) this.ShowSchoolList = true;
        },

        "WindowLoad": function() {
			csGlobalJs.OpenInNewWindowWarning();
        },

        "WindowResize": function() {
            this.JsMediaQueries();
            if($(".hp").length) {
                this.CheckSlideshow();
            }
        },

        "WindowScroll": function() {
            if($(".hp").length) {
                this.CheckSlideshow();
            }
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 
					var getWindowHeight = $(window).height();
                    var getChannelHeight = $(".sw-channel-list").height();
                    var getSocialMediaHeight = $("#gb-social-media-icons").height();
                    var getSidebarminusChannelHeight = getWindowHeight - getSocialMediaHeight - 134;
                    if(getChannelHeight > getSidebarminusChannelHeight) {
                    	$(".gb-page.left").addClass("channel-fallback");
                    } else {
                    	$(".gb-page.left").removeClass("channel-fallback").removeAttr("style");
                    }
                break;
                case "768":
                case "640":
                case "480":
                case "320":
					if($(".gb-page.left").hasClass("channel-fallback")) {
                    	$(".gb-page.left").removeClass("channel-fallback").removeAttr("style");
                    }
                break;
            }
        },

        "MyStart": function() {
        	// FOR SCOPE
            var _this = this;

			// ADD DISTRICT HOME
            if(this.ShowDistrictHome && $(".sw-mystart-button.home").length) {
            	$(".sw-mystart-button.home > a > span").text("District Home");
            	$(".gb-header.right #gb-mystart-right").append($(".sw-mystart-button.home"));
            }

            // BUILD USER OPTIONS DROPDOWN
            var userOptionsItems = "";



			// SIGNIN BUTTON
            if($(".sw-mystart-button.signin").length) {
				userOptionsItems += "
