$('.schoolList').each(function(){

    var myList = $(this);
    var listItems = myList.children('li:not(.districtSite)').get();

    listItems.sort(function (a, b){
        return $(a).text().toUpperCase().localeCompare($(b).text().toUpperCase());
    });

    $.each(listItems, function(index, item){
        myList.append(item);
    });
});

