  $('#filterOptions > li button').click(function(e) {
    e.preventDefault();
    // fetch the class of the clicked item
    var ourClass = $(this).attr('class');

    // reset the active class on all the buttons
    $('#filterOptions li.active').removeClass('active');
    // update the active state on our clicked button
    $(this).parent().addClass('active');

    if(ourClass == 'all') {
      // show all our items
      $('.post .panel').children('div.school-row').show();
    }
    else {
      // hide all elements that dont share ourClass
      $('.post .panel').children('div:not(.' + ourClass + ')').hide();
      // show all elements that do share ourClass
      $('.post .panel').children('div.' + ourClass).show();
    }
    return false;
  });

  //Filter CIP - year
  $('#filterOptions .yearFilter li button').click(function(e) {
    e.preventDefault();
    // fetch the class of the clicked item
    var yearClass = $(this).attr('class');

    // reset the active class on all the buttons
    $('#filterOptions .yearFilter li.active').removeClass('active');
    // update the active state on our clicked button
    $(this).parent().addClass('active');

    if(yearClass == 'all') {
      // show all our items
      $('.post').show();
    }
    else {
      // hide all elements that dont share yearClass
      $('.post:not(.' + yearClass + ')').hide();
      // show all elements that do share yearClass
      $('.post.' + yearClass).show();
    }
    return false;
  });

