$(document).ready(function(){
  $('.menu_toggle').click(function() {
    if($(this).children("img").attr("src") == "http://www.projectbread.org/assets/images/green-plus.gif") {
      $(this).children("img").attr("src", "http://www.projectbread.org/assets/images/green-x.gif");
    } else {
      $(this).children("img").attr("src", "http://www.projectbread.org/assets/images/green-plus.gif")
    }
    $(this).parent().children('ul').slideToggle('slow', function() {});
  });
});
function validateEmailAddress() {
  var x = document.mob_enews.cons_email.value;
  if (x == null || x == "" || x == "E-news sign-up") {
    alert("Please enter your Email Address.");
    document.mob_enews.cons_email.focus();
    return false;
  } else {
    var atpos = x.indexOf("@");
    var dotpos = x.lastIndexOf(".");
    if (atpos < 1 || dotpos < atpos + 2 || dotpos + 2 >= x.length) {
      alert("Please enter a valid e-mail address");
      document.mob_enews.cons_email.focus();
      document.mob_enews.cons_email.select();
      return false;
    }
  }
}

