$( 'body' ).on( 'click', '[data-toggle="modal"]', function(){
let data_remote = $(this).data( "remote" );
if ( data_remote && data_remote.length >= 2 ) {
$($(this).data( "target" ) + ' .modal-content').load( data_remote );
}
});
