jQuery( function( $ ) {
	var $primary_nav_and_button = $( '.primary-nav-button, .primary-nav-mobile' ),
		$baton_col_quick_links_search_form = $( '.primary-nav-mobile-container .baton-col-quick-links-search-form' );

	// Primary Nav
	$primary_nav_and_button.on( 'click', function ( event ) {
							// Prevent Propagation (bubbling) to other elements and default
			event.stopPropagation();
			event.preventDefault();

		// Open
		if ( ! $primary_nav_and_button.hasClass( 'open' ) ) {
			$baton_col_quick_links_search_form.addClass( 'open' );

			// 500ms delay to account for CSS transition (if any)
			setTimeout( function() {
				$baton_col_quick_links_search_form.addClass( 'opened' );
			}, 500 );
		}
		// Close
		else {
			$baton_col_quick_links_search_form.removeClass( 'open opened' );
		}
	} );

	// Click
	$baton_col_quick_links_search_form.on( 'click', function( event ) {
		// Prevent Propagation (bubbling) to other elements
		event.stopPropagation();
		event.stopImmediatePropagation();
	} );
} );
