    $('#medicalalpha > li > a').on("click", function () {
        var idclick = $(this).text();
        $(".alpha-results-row > div").each(function (item) {
            $(this).hide();
        });

        $(".alpha-results-row > div#" + idclick).show();

    });

    $("#navnewssearch").keypress(function (e) {
        if ((e.which && e.which == 13) || (e.keyCode && e.keyCode == 13)) {
            $("#btnnewssearch").click();
            return false;
        } else {
            return true;
        }
    });
    $("#btnnewssearch").click(function (e) {
        var urlref = "/how-we-compare/newsroom/Search/?rnd=1234&Specialty=&keyword=" + $("#navnewssearch").val() + "&AndTags=&OrTags=&categoriesAnd=&categoriesOr=";
        window.location.href = urlref;
        e.preventDefault();
    });

