    $(document).ready(function () {

        $(document).on("click", ".modalslider", function (e) {
              var ATTRIBUTES = ['title', 'summary','publisheddate'];
            var $target = $(e.target);
            // modal targeted by the button
            var modalSelector = $target.data('target');

            var videoid = $target.data('id');
            var videourl = $target.data('videoid');
            var videotitle = $target.data('title');
            var videosummary = $target.data('summary');
            var videopublisheddate = $target.data('publisheddate');

            var framevideo = "https://www.youtube.com/embed/" + videourl + "?enablejsapi=1&rel=0&showinfo=0";
            if (typeof videotitle != 'undefined') {
                    var trackTitle = videotitle.replace(/ /g, "_");
                    var pixelUrl = "http://www.google-analytics.com/collect?v=1&tid=UA-34251162-1&cid=LakelandUser&t=event&ec=HomeScreenPopUps&ea=Impression&cs=SpectrumHealthLakeland&cn=StartWatching&cm=WebUser&el=" + trackTitle +"&n=%n&ord=%%CACHEBUSTER%%";
                    $("#TrackingPixel").attr("src", pixelUrl);
            }

            ATTRIBUTES.forEach(function (attributeName) {
               // retrieve the dom element corresponding to current attribute
                var $modalAttribute = $(modalSelector + ' #modal-' + attributeName);

                var dataValue = $target.data(attributeName);
                if (typeof dataValue != 'undefined') {
                    dataValue=dataValue.replace(/( |(<([^>]+)>))/ig, '');
                }
               $modalAttribute.text(dataValue || '');
            });
            


