link.script:
$.fn.cycle.defaults.timeout = 6000;
$(function() {
    // run the code in the markup!
    $('table pre code').not('#skip,#skip2').each(function() {
        eval($(this).text());
    });
    
    $('#s4').before('
').cycle({
        fx:     'scrollLeft',
        speed:  'slow',
		delay:   -8000,
        pager:  '#newsnav'
		
    });
	
    
    $('#s6').before('
').cycle({
        fx:     'scrollLeft',
        speed:  'slow',
		timeout: 0,
		random:  1 ,
        pager:  '#nav'
		
    });
	
});

function onBefore() {
    $('#output').html("Scrolling image:
" + this.src);
    //window.console.log(  $(this).parent().children().index(this) );
}
function onAfter() {
    $('#output').html("Scroll complete for:
" + this.src)
        .append('
' + this.alt + '');
}

function initScrollLayer() {
    var wndo = new dw_scrollObj('wn', 'lyr1', 't1');
    dw_scrollObj.GeckoTableBugFix('wn');
}

$(document).ready(function() {
        // Tooltip only Text
        $('.masterTooltip').hover(function(){
                // Hover over code
                var title = $(this).attr('title');
                $(this).data('tipText', title).removeAttr('title');
                $('
')
                .text(title)
                .appendTo('body')
                .fadeIn('slow');
        }, function() {
                // Hover out code
                $(this).attr('title', $(this).data('tipText'));
                $('.tooltip').remove();
        }).mousemove(function(e) {
                var mousex = e.pageX + 20; //Get X coordinates
                var mousey = e.pageY + 10; //Get Y coordinates
                $('.tooltip')
                .css({ top: mousey, left: mousex })
        });
});

    $(function () {
        $('a.simplemodal').live('click', function () {
            $.get(this.href, function (data) {
                $.modal(data, {
                    onShow: function (dialog) {
                        // create the 'onShowDialog' function in the page loaded via Ajax 
                        // (usually used to initialize more jQuery stuff like DatePickers in the modal dialog)
                        onShowDialog(dialog);
                    },
                    maxHeight: 400,
                    minHeight: 400,
                    maxWidth: 600,
                    minWidth: 600
                });
            });
            return false;
        });
		

    });

