		{ mediaRotator: "//extend.schoolwires.com/creative/scripts/creative/rotate/cs.rs.multimedia.rotator.v2.min.js" },
		{ globalIcons: "//extend.schoolwires.com/creative/scripts/creative/tools/creative-icons-v2/creativeIcons.v2.min.js" },
		{ tabBlocks: "//extend.schoolwires.com/creative/scripts/creative/responsive/cs.rs.tabbed.blocks.min.js" },
		{ modEvents: "//extend.schoolwires.com/creative/scripts/joel/mod-events/joel.mod-events.min.js" }
	]);

	$(function(){

		$(window).bind("resize", function(){ jsMediaQueries();stickyCalendarFix(); });


		head.ready("rsMainMenu", function(){
			var additionalHeaders = "", additionalLinks = "";

			// MENU GLOBAL ICONS
			var menuGlobalIcons = [
				["District Directory", "https://www.trentonschools.com/directory"],
				["District Calendar", "[SITECALENDARLINK]"],
				["Menus", "https://www.trentonschools.com//Page/2902"],
				["MISTAR", "/Page/66"]
			];
			var menuIconNum = ("4" == "4;3;2;1;0") ? 0 : "4";

			for(var i = 0; i < menuIconNum; i++) {
				var linkCheck = (menuGlobalIcons[i][1] == "[SITECALENDARLINK]") ? "/Page/2" : menuGlobalIcons[i][1];
				additionalLinks += "4, " + menuGlobalIcons[i][0] + ", " + linkCheck + ", _self;";
			}

			if(menuIconNum > 0) {
				additionalHeaders = "Popular Links";
			}

			// RESPONSIVE MAIN MENU
			$("body").csRsMenu({
			        "breakPoint"            : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			        "slideDirection"        : "right-to-left", // OPTIONS - left-to-right, right-to-left   
			        "primaryColor"          : "#2C363E", // DEFAULT - #2C363E
			        "secondaryColor"        : "#222A30", // DEFAULT - #222A30
			        "accentColor"           : "#B5BBBF", // DEFAULT - #B5BBBF
			        "menuButtonParent"      : ".gb-mystart.rsmenu",
			        "showMenuBtnText"       : "yes",
			        "menuBtnText"           : "MENU",
			        "showSchools"           : "yes",
			        "showTranslate"         : "yes",
			        "translateId"           : "none",
			        "showAccount"           : "yes",
			        "extraMenuOptions"      : additionalHeaders, // EXTRA MENU HEADING NAMES SEPARATED BY A ;
			        "extraMenuItems"        : additionalLinks, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
			        "siteID"                : "4"
			});

		});

		head.ready("rsPagelistMenu", function(){
			// RESPONSIVE PAGELIST MENU
			$("body").csRsPagelistMenu({
			        "breakPoint"            : 640, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			        "slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left
			        "primaryColor"          : "#2C363E", // DEFAULT - #2C363E
			        "secondaryColor"        : "#222A30", // DEFAULT - #222A30
			        "accentColor"           : "#B5BBBF", // DEFAULT - #B5BBBF
			        "menuButtonParent"      : "#sp-content",
			        "showMenuBtnText"       : "yes",
			        "menuBtnText"           : "PAGES",
			        "siteID"                : "4"
			});
		});	

		head.ready("globalIcons", function() {
			var csIconNum = ("4" == "4;3;2;1;0") ? "0" : "4";
			$(".gb-global-icons").creativeIcons({
			        "iconNum"      : csIconNum, // NUMBER OF ICONS
			        "iconColor"    : "#e5aa37", // ICON COLOR
			        "icon1"        : ["/cms/lib8/MI01908975/Centricity/Template/GlobalAssets/icons/global1.png", "District Directory", "https://www.trentonschools.com/directory", "_blank"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			        "icon2"        : ["Site Utility/1.png", "District Calendar", "[SITECALENDARLINK]", "_blank"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			        "icon3"        : ["Student Program/5.png", "Menus", "https://www.trentonschools.com//Page/2902", "_blank"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			        "icon4"        : ["/cms/lib8/MI01908975/Centricity/Template/GlobalAssets/icons/global2.png", "MISTAR", "/Page/66", "_blank"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			        "siteID"       : "4",
			        "siteAlias"    : "TrentonPublicSchools",
			        "calendarLink" : "/Page/2",
			        "contactEmail" : "tps-website@trentonschools.com",
			        "onComplete"   : function(){ }
			});
		});

		jsMediaQueries();
		useChannelForSection();
		dynamicContent();
		modEvents();
		addFramesetTranslate();
        stickyCalendarFix();

		$("body").csRsSameHeights({
		        resizeElements : [
		                {
		                        "resizeMethod"          : "columns",            // OPTIONS - columns, apps
		                        "parent"                : "#hp-content",        // PARENT THAT COLUMNS AND/OR APPS ARE IN THAT ARE GETTING RESIZED
		                        "columns"               : ".hp.column",         // COLUMNS AND/OR APPS WITHIN THESE COLUMNS THAT SAME HEIGHTS IS BEING APPLIED TO -- NEEDS TO BE A CLASS
		                        "gutter"                : 0,                   // PIXEL SPACING BETWEEN APPS -- WILL BE ADDED AS BOTTOM MARGIN
		                        "applySameHeights"      : ["960"]        // OPTIONS - ["960", "768", "640", "480", "320"]
		                }
		        ]
		});

	});

	function jsMediaQueries(){
		switch(csGetBreakPoint()) {
			case "desktop": 
				mediaRotator();
				tabbedApps();
				$(window).scroll(parallaxScroll);
				upcomingEventsSameHeight();
				autoScroll();
				$(".gb-mystart.right").show();
			break;
			case "768":
				mediaRotator();
				appView();
				if(!$("#sw-mystart-mypasskey").length && !$(".sw-mystart-button.pw").length){
					$(".gb-mystart.right").hide();
				}
			break;
			case "640":
				mediaRotator();
				appView();
				if(!$("#sw-mystart-mypasskey").length && !$(".sw-mystart-button.pw").length){
					$(".gb-mystart.right").hide();
				}
			break;
			case "480":
				appView();
				if(!$("#sw-mystart-mypasskey").length && !$(".sw-mystart-button.pw").length){
					$(".gb-mystart.right").hide();
				}
			break;
			case "320":
				appView();
				if(!$("#sw-mystart-mypasskey").length && !$(".sw-mystart-button.pw").length){
					$(".gb-mystart.right").hide();
				}
			break;
		}
	}

