		//ADD TRANSPARANCY LOGO
		var transSrc = jQuery.trim("/cms/lib/MI01908975/Centricity/Template/GlobalAssets/images///icons/Budget-Logo-2017.gif");
		var transSrcSplit = transSrc.split("/");
		var transSrcSplitLen = transSrcSplit.length;
		if((transSrc != "") && (transSrcSplit[transSrcSplitLen - 1] != "default-man.jpg")) {
			$("#hp-slideshow .transparency-image").append('').removeClass("hidden");
		} else {
			$("#hp-slideshow .transparency-image").append('').removeClass("hidden");
