		$("a#feedback").attr("href", feedbackURL);
		$("a#terms").attr("href", termsURL);
		$("a#policy").attr("href", policyURL);	
		$("a#swlogo").attr("href", swURL);		
	}

	function upcomingEventsSameHeight() {
		$(".hp .ui-widget.app.upcomingevents .ui-articles").each(function(){
			var items = $('li',this);
			var total = items.size();
			var maxHeight = 0;
			for(i = 0; i < total; i+=1){
			    items.each(function() {
                	$(this).css('height', 'auto'); //-- Added by Chris  Case 00242161 Gets Elements true height.
			        maxHeight = $(this).height() > maxHeight ? $(this).height() : maxHeight;
			    }).height(maxHeight);
			}
		});

	}

    function stickyCalendarFix() {
    	var headerHeight = $("#gb-mystart-outer").height();
        var channelHeight = $("#gb-channel-list-outer").height();
        var calendarHeaderOffset = headerHeight + channelHeight;

        if ($(window).scrollTop() <= calendarHeaderOffset) {

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                $(".wcm-controls").css("margin-top", "0px");
            }
        } else {

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                if($(".wcm-controls").hasClass("wcm-stuck")) {
                    $(".wcm-controls").css("margin-top", calendarHeaderOffset + "px");
                }
            }
        }
    }

	function smoothScroll( to, offset ){
	    if( to.indexOf("#") < 0 ){ to = "#" + to; } //makes sure we have a valid id accessor string
	    var target = $(to);

	    if (target.length) {
	        $('html,body').animate({
	            scrollTop: target.offset().top - offset //this offset accounts for the fixed menu on the top
	        }, 800, function(){
	            $("html, body").stop(true, false);
	        });
	        return false;
	    }
	}

	function autoScroll() {
		var lastScrollTop = 0;
		$(window).scroll(function(event){
			var st = $(this).scrollTop();
			if (st > lastScrollTop){
				var slideshowHeight = $("#hp-slideshow").height() - 150;
				var headerHeight = $("#gb-sticky-header").height() + 30;
				if($(window).scrollTop() < slideshowHeight) {
					smoothScroll("#hp-content-outer", headerHeight);
				}	
			} else {
				// upscroll code
			}
			lastScrollTop = st;
		});
	}

	function searchText() {
		//CUSTOM MYSTART SEARCH PLACEHOLDER
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "What are you searching for?");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "What are you searching for?") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("What are you searching for?");
			}
		});
	}

	function modEvents(){
		head.ready("modEvents", function() {
			if (!$(".upcomingevents .joel-month").length) {
				$(".upcomingevents").modEvents({
					columns     : "yes",
					monthLong   : "no",
					dayWeek     : "no"
				});
				eventsByDay(".upcomingevents .ui-articles");
			}
		});
		$("div.ui-widget.app.upcomingevents .ui-widget-detail .ui-articles").each(function(){
			$(this).addClass("ui-clear");
		});
	}		

	function eventsByDay(container) {
	    var countLi = 0;
	    $("li", container).each(function(){
	        if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
	        	var prevDate = $(this).prev().find("h1.ui-article-title.sw-calendar-block-date").html();

	          $('.ui-article', this).addClass('upcoming-column-container ui-clear').prepend('

' + prevDate + '');
	      		$('.ui-article-description', this).wrap('
');
	        };
	    });
	}


	function calendarListView(){
		//AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}

	function csGetBreakPoint() {
		return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
	}

	var parallaxScroll = function(){
		var getWidth = $(window).width();
		if (getWidth > 768) { // 960+
			if($(window).scrollTop() > 0) {
				$("#gb-logo-outer").fadeOut();
				$("#gb-sitename").fadeOut();
				$("#gb-logo-sticky").fadeIn();  

			} else {
				$("#gb-logo-outer").fadeIn();
                if($('#gb-sitename').text().length > 0){
					$("#gb-sitename").fadeIn();
                }
				$("#gb-logo-sticky").fadeOut();  
			}
		}

	}

