	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+

		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			imgSlider();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			imgSlider();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			imgSlider();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			imgSlider();
		}
	}

	function photoGallery(){
		head.ready("multimediaRotator", function(){
            if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
                $("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
                    "efficientLoad" : "yes",
                    "imageWidth" : 960,
                    "imageHeight" : 405,
                    "appHeader" : "no",
                    "titles" : "yes",
                    "captions" : "yes",
                    "galleryOverlay" : "no",
                    "linkedElement" : ["image"], // ["image", "title", "overlay", "all"]
                    "playPause" : "yes",
                    "mainControls" : "no",
                    "contMoreLink" : "no",
                    "contMoreLinkText" : "Read More",
                    "descMoreLink" : "no",
                    "descMoreLinkText" : "Read More",
                    "descVideoLink" : "no",
                    "descVideoLinkText" : "Watch Video",
                    "bullets" : "yes",
                    "bulletControls" : "yes",
                    "thumbnails" : "no",
                    "thumbnailWidth" : 122,
                    "thumbnailHeight" : 53,
                    "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
                    "hoverPause" : "yes",
                    "fullScreenRotator" : "no", // image dimensions work well at 1500x915
                    "fullScreenBreakpoints" : ["960", "768", "640", "480", "320"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
                    "fullScreenVideo" : "no",
                    "transitionType" : "fade", // fade or slide
                    "transitionSpeed" : 2,
                    "transitionDelay" : 8,
                    "autoRotate" : "yes",
                    "customResize" : "no",
                    "customResizeFunction" : function(element) {},
                    "onImageLoad" : function() {
                    	// ADD IMAGE 
 TITLE ATTRIBUTES
                        // ADDED BY BRENTON KELLY - CASE: 00475357 - 2.23.17
                    	var titleAttr = $(this).closest(".image").attr("title");
                        if (titleAttr === undefined || titleAttr === false) {
                            $(this).closest(".image").attr("title", $(this).attr("alt"));
                        }
                    },
                    "allImagesLoaded" : function() { },
                    "onThumbnailLoad" : function() { },
                    "allThumbnailsLoaded" : function() { },
                    "onTransitionStart" : function() { },
                    "onTransitionEnd" : function() { },
                    "watchVidClick" : function() { },
                    "closeVidClick" : function() { },
                    "allLoaded" : function() {
                    	$("#hp-slideshow .rs-gallery-desc").prepend('');
						initSlideshowBG();

                        $("#hp-slideshow .rs-gallery-container").prepend($("#hp-slideshow .rs-gallery-control.play-pause"));
                    }
                });
            }
		});
	}	

