        	$(".sw-channel-item.search").on("mousedown keydown", "a", function(e) {
                if(allyClick(e) || e.type == "mousedown") {
                	e.preventDefault();

                    if($(this).attr("aria-expanded") == "false") {
                    	$(this).attr({
                        	"aria-label": "Search Dialog",
                            "aria-expanded": "true"
                        });

                        $("#gb-search").slideDown(function() {
                        	if(e.type == "keydown" && (e.keyCode == csKeyCodes.space || e.keyCode == csKeyCodes.enter)) {
                            	$("#gb-search .gb-search-input").focus();
                            }
                        }).attr("aria-hidden", "false");
                    } else {
                    	$(this).attr({
                        	"aria-label": "Search Dialog",
                            "aria-expanded": "false"
                        });

                        $("#gb-search").slideUp().attr("aria-hidden", "true");
                    }
                } else if(e.type == "keydown" && !e.shiftKey && e.keyCode == csKeyCodes.tab && $("#gb-search").attr("aria-hidden") == "false") {
                	e.preventDefault();
                    $("#gb-search-input").focus();
                }
        	});

            $(document).on("click keydown", ".gb-channel-list-icon.search a", function(e) {
                if(allyClick(e)) {
                	e.preventDefault();

                    if($(this).attr("aria-expanded") == "false") {
                    	$(this).attr({
                        	"aria-label": "Search Dialog",
                            "aria-expanded": "true"
                        });

                        $("#gb-search").slideDown(function() {
                        	if(e.type == "keydown" && (e.keyCode == csKeyCodes.space || e.keyCode == csKeyCodes.enter)) {
                            	$("#gb-search .gb-search-input").focus();
                            }
                        }).attr("aria-hidden", "false");
                    } else {
                    	$(this).attr({
                        	"aria-label": "Open Search Dialog",
                            "aria-expanded": "false"
                        });

                        $("#gb-search").slideUp().attr("aria-hidden", "true");
                    }
                } else if(e.type == "keydown" && !e.shiftKey && e.keyCode == csKeyCodes.tab && $("#gb-search").attr("aria-hidden") == "false") {
                	e.preventDefault();
                    $("#gb-search-input").focus();
                }
        	});

            $("#gb-search-input").keydown(function(e) {
                if(e.type == "keydown" && e.shiftKey && e.keyCode == csKeyCodes.tab) {
                	e.preventDefault();

                    var getWidth = $(window).width();
					if (getWidth >= 640) { // 640+
                        $(".sw-channel-item.search > a").focus();
                    } else {
                    	$(".gb-channel-list-icon.search a").focus();
                    }
                }
            });

            $("#gb-search-button").keydown(function(e) {
                if(e.type == "keydown" && !e.shiftKey && e.keyCode == csKeyCodes.tab) {
                	e.preventDefault();

                    var getWidth = $(window).width();
					if (getWidth >= 640) { // 640+
                        $(".sw-channel-item.signin > a").focus();
