	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
			$("a.ui-btn-toggle.list").click();
			$("a.ui-btn-toggle.month").click();
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
		}

		var getWidth = $(window).width();
		if(getWidth >= 640){ // 640+
			$(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
		} if(getWidth < 640){ // 480-
			$(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
		}
	}

	function footerImages(){
		// CHECK TO SEE WHAT IMAGES ARE SET
		var images = [
			'/cms/lib/MN01909485/Centricity/Template/GlobalAssets/images///District Seasonal Images/Distance Learning.png',
			'/cms/lib/MN01909485/Centricity/Template/GlobalAssets/images///District Seasonal Images/Student support.png',
			'/cms/lib/MN01909485/Centricity/Template/GlobalAssets/images///District Seasonal Images/Meals.png',
			'/cms/lib/MN01909485/Centricity/Template/GlobalAssets/images///District Seasonal Images/Child care.png'
		]

        var imagesAlt = [ //added imagesAlt by Lyndsay - Case 00475357 - 2.1.17
			"Distance learning",
			"Student support",
			"Child nutrition, meals",
			"Child care"
		]

		var i = 0
		$(".hp-image-slide a").each(function(index){
			if(i < 4){
				var slideContainer = $(this);

				if(images[i] != ""){
					$("").load(function(){ //replaced alt="slider images" with imagesAlt by Lyndsay - Case 00475357 - 2.1.17
						$(this).appendTo(slideContainer);
						slideContainer.parent().removeClass("hidden");
					});
				}
				i++;
			} else {
				return false;
			}
		});
	}

	function imgSlider(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			$("#hp-image-slider, #hp-image-slider-inner").attr("class", "ui-clear start960");
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
