	function channelMod(){
	        // RESET THE MARGIN
	        $("li.sw-channel-item").css("margin", "");

	        // SET UP OUR VARIABLES
	        var channelNum = $("li.sw-channel-item").length, channelWidths = 0, channelBarWidth = $("#gb-channel-list").width(), availSpace = 0, channelPad = 0;

	        // GET THE TOTAL WIDTH OF ALL THE CHANNELS
	        $("li.sw-channel-item").each(function(){
	                channelWidths += $(this).width();
	        });

	        // GET THE TOTAL AMOUNT OF EMPTY SPACE LEFT OVER
	        availSpace = channelBarWidth - channelWidths;

	        // GET THE AMOUNT OF SPACE TO SET BETWEEN EACH CHANNEL
	        // SUBTRACT ONE FROM THE TOTAL CHANNEL NUMBER BECAUSE WE WANT THE LAST CHANNEL TO SIT AGAINST THE RIGHT EDGE OF THE TEMPLATE
	        channelPad = Math.floor(availSpace / (channelNum - 1)) - .5;

	        // NOW APPLY THE MARGIN
	        $("li.sw-channel-item").not($("li.sw-channel-item.last")).css("margin-right", channelPad + "px");

	        // SHOW THE CHANNEL BAR NOW THAT WE ARE DONE MESSING WITH THINGS
	        // ONLY MATTERS FOR INITIAL LOAD
	        //$("#gb-channel-list").removeClass("invisible");
	}

	function imgResize(){
		head.ready("photoGallery", function(){
			var imgWidth = $("#hp-slideshow .rotating-images ul.pictures li").width();

			var initW = 800, initH = 405;
			var ratio = initH / initW;

			var imgHeight = imgWidth * ratio;

			$("#hp-slideshow, #hp-slideshow .rotating-images ul.pictures li").css("height", imgHeight);
		});
	}

	function initSlideshowBG() {
		if($(".hp").length){
			var canvas = document.getElementById("hp-canvas");

			//IF IE
			if (typeof(G_vmlCanvasManager) != "undefined"){G_vmlCanvasManager.initElement(canvas);}
			var ctx = canvas.getContext("2d");

			drawSlideshowBG(ctx);
		}
	}

	function drawSlideshowBG(ctx) {
		var slideshowBGColor = "rgba(" + hexToRgb('#0f3857') + ", 0.50)";

		// layer1/Path
		ctx.save();
		ctx.beginPath();
		ctx.moveTo(3.4, 18.7);
		ctx.lineTo(305.2, 0.0);
		ctx.bezierCurveTo(307.9, -0.2, 310.0, 1.5, 310.0, 3.7);
		ctx.lineTo(310.0, 232.6);
		ctx.bezierCurveTo(310.0, 234.8, 307.9, 236.5, 305.2, 236.3);
		ctx.lineTo(3.4, 217.6);
		ctx.bezierCurveTo(1.5, 217.5, 0.0, 215.9, 0.0, 214.1);
		ctx.lineTo(0.0, 22.3);
		ctx.bezierCurveTo(0.0, 20.4, 1.5, 18.8, 3.4, 18.7);
		ctx.closePath();
		ctx.fillStyle = slideshowBGColor;
		ctx.fill();
		ctx.restore();
	}

