$(document).ready(function(){
    $("table").find("table[cellpadding]").each(function(i){
        var cellPad = $(this).attr("cellpadding");
        $(this).find("td,th").css({
            "padding" : cellPad
        });
    });
    $("table").find("table[cellspacing]").each(function(i){
        var cellSpace = $(this).attr("cellspacing");
        $(this).css({
            "border-spacing" : cellSpace+"px",
        });
    });

    $("table").find("table[border]").each(function(i){
        var border = $(this).attr("border");
        $(this).css({
            "border" : border+"px solid #ccc",
            "border-collapse" : "separate"
        });
    });
});

$(document).ready(function(){

// press enter key to open dropdown menu
var toggled = false;
$('ul.navbar-nav > li > a').keydown(function(e) {
    if(e.keyCode == 13 && $(this).find('ul.sub-menu') ) { // enter key
        toggled = !toggled;
        e.preventDefault();
        $('ul.navbar-nav > li').not($(this).parent()).removeClass('force-show');
        $('ul.navbar-nav > li > a').next().not($(this)).css('display','none');
        // $(this).parent().attr('class', toggled ? 'force-show':'');
        $(this).parent().addClass('force-show');
        $(this).next().css('display','block');
        $(this).attr('aria-expanded', toggled ? 'true':'false');
    }
    if(e.keyCode == 40) { // down arrow, focus to first link
        e.preventDefault();
        e.stopPropagation();
        $(this).next().find('a').first().focus();
    }
});
// For sub menu level 2
$('ul.dropdown-menu li.dropdown a').keydown(function(e){
    if($(this).attr('href') === '#' || $(this).attr('href') === 'javascript:void(0);'){
        if(e.keyCode == 13){ // enter key
            e.preventDefault();
            e.stopPropagation();
            var left = $(this).outerWidth();
            var top = $(this).position().top;

            $('ul.dropdown-menu li.dropdown').removeClass('force-show');
            $('ul.dropdown-menu li.dropdown a').next().not($(this)).css('display','none');
            $(this).parent().addClass('force-show');
            $(this).next().css({
                display:'block',
                left: left,
                top: top
            });
        }
        if(e.keyCode == 40) { // down arrow, focus to first link
            e.preventDefault();
            e.stopPropagation();
            $(this).next().find('a').first().focus();
        }
    }
});

// Bug 84403
var mediaQuery = window.matchMedia('(max-width: 767px)');
if(mediaQuery.matches){
    $('ul.dropdown-menu').unwrap();
    $('ul.navbar-nav > li.dropdown > a').attr('href','#');
    $('ul.navbar-nav > li.dropdown > a').attr('data-toggle', 'dropdown');
}

$('.dropdown-menu > li.dropdown > a').mouseover(function() {
    var el = $(this).next('.wrapper');
    var offset = el.offset();

    var val = parseInt($(this).outerWidth()) + parseInt($(this).position().left);

    if(parseInt($(this).offset().left) + parseInt($(this).outerWidth()) + parseInt(el.outerWidth()) > $(document).width() || parseInt(el.css('left')) < 0 ) {
        el.css({
            right: val,
            left: 'auto'
        });
    } else {
        el.css({
            right: 'auto',
            left: val
        });
    }

});

    // access search input
    $('#search-form').keypress(function(e){
        if(e.keyCode == 13){ // enter key
            e.preventDefault();
            $(this).addClass('openclick');
        }
    });
    // add href attribute to all nav menu
    $('ul.navbar-nav a:not([href])').attr('href','#');
    // press shift+tab key focus to previous link
    $('body').keypress(function (e){
        if(e.shiftKey && e.keyCode == 9) {
            e.preventDefault();
            e.stopPropagation();
            $(this).closest('li').prev().find('a').focus();
        }
    })
    // press enter key to open dropdown menu
    var toggled = false;
    $('ul.navbar-nav > li > a').keydown(function(e) {
        if(e.keyCode == 13 && $(this).next().hasClass('sub-menu') !== false ) { // enter key
            toggled = !toggled;
            e.preventDefault();
            $('ul.navbar-nav > li').not($(this).parent()).removeClass('force-show');
            // $(this).parent().attr('class', toggled ? 'force-show':'');
            $(this).parent().addClass('force-show');
            $(this).attr('aria-expanded', toggled ? 'true':'false');
        }
        if(e.keyCode == 40) { // down arrow, focus to first link
            e.preventDefault();
            e.stopPropagation();
            $(this).next().find('a').first().focus();
        }
    });
    // press up/down & end/home key to access submenu
    $('.dropdown-menu > li > a').keydown(function(e){
        if(e.keyCode == 40) { // down arrow
            e.preventDefault();
            e.stopPropagation();
            $(this).closest('li').next().find('a').focus();
        }
        if(e.keyCode == 38) { // up arrow
            e.preventDefault();
            e.stopPropagation();
            $(this).closest('li').prev().find('a').focus();
        }
        if(e.keyCode == 36) { // home key
            e.preventDefault();
            $(this).closest('.dropdown-menu').find('a:first').focus();
        }
        if(e.keyCode == 35) { // end key
            e.preventDefault();
            $(this).closest('.dropdown-menu').find('a:last').focus();
        }
        // For sub menu level 2
        if($(this).attr('href') === '#'){
            if(e.keyCode == 13){ // enter key
                e.preventDefault();
                e.stopPropagation();
                $('.dropdown-menu > li').removeClass('force-show');
                $(this).parent().addClass('force-show');
            }
            if(e.keyCode == 40) { // down arrow, focus to first link
                e.preventDefault();
                e.stopPropagation();
                $(this).next().find('a').first().focus();
            }
        }
    })
    $('body').click(function () {
        $('*').removeClass('force-show');

    });
});

                    $(document).ready(function(){
                        var skipId = $('.inner-content').attr('id');;
                        $('body').prepend('
Skip to main content');

                        $('a.skip-nav').click(function(event) {
                            var height = parseInt($('.sticky-hs').outerHeight());
                            if(isNaN(height) || height === null){
                                height = parseInt($('.bottom-header').outerHeight());;
                            }else{
                                height = height;
                            }
                            if (this.hash !== '') {
                                event.preventDefault();
                                var hash = this.hash;
                                $('html, body').animate({
                                  scrollTop: $(hash).offset().top - height
                                }, 500, function(){
                                  window.location.hash = hash;
                                });
                                console.log(height);
                            }

                        });
                    });

$(document).ready(function(){
    var height= $(".bottom-header").outerHeight();
    $("header").css("margin-top", height);

    // Bug 83106
    $(".wrapper > ul.dropdown-menu > li.dropdown").mouseover( function() {
        var menuItem = $(this),
            parentMenuItem = $(this).closest(".wrapper"),
            submenuWrapper = $("> .wrapper", menuItem);
        var menuItemPos = menuItem.position();
        var parentMenuItemPos = parentMenuItem.position();
        var max = 400;
        var headerHeight = $(".bottom-header").outerHeight();
        var submenuDropdown = $("> .wrapper > ul.dropdown-menu", menuItem);

        var menuHeight = Math.round(menuItemPos.top) + Math.round(parentMenuItemPos.top) + Math.round(submenuWrapper.outerHeight()) + Math.round(headerHeight);
        var windowHeight = $(window).height();

        if(menuHeight > windowHeight){
            max = windowHeight - Math.round(parentMenuItemPos.top) - Math.round(menuItemPos.top) - Math.round(headerHeight);
        }
        submenuDropdown.css({
            "max-height": max,
            "min-height": "34px"
        });
        submenuWrapper.css({
          top: menuItemPos.top
        });
    });
});

