$(function(){
  $('div.subheader_cont div:nth-of-type(1) div:nth-of-type(1)').addClass("full_slideshow");
  $('div.subheader_cont div:nth-of-type(1) div:nth-of-type(1)').attr('style', 'max-width:100% !important;max-height:100%!important;');
  $('.sub-menu').click(function(event){
      event.stopPropagation();
      var dropdown = $(this).children('.dropdown-menu');
      dropdown.toggle();
  });
  $('.sub-menu').parent().click(function() {
      var menu = $(this).children("ul");
      var menupos = $(menu).offset();
           
      if (menupos.left + menu.width() > $(window).width()) {
          var newpos = -$(menu).width();
          menu.css({ left: newpos });    
      }
  });
});

    //This scipt for Bootstrap  sub-menu dropdown on mobile view//
    (function($){
        $(document).ready(function(){
            $('ul.dropdown-menu [data-toggle=dropdown]').on('click', function(event) {
            event.preventDefault(); 
            event.stopPropagation(); 
            $(this).parent().siblings().removeClass('open');
            $(this).parent().toggleClass('open');
            });
          });
     })(jQuery);

