        function openButton(button_type,blog_id) {
            var the_url = "/ajax/GetWikiURL";
            var the_name = "wiki_".blog_id;
            if (button_type == 2) {
            	the_url = "/ajax/GetBlogURL";
            	the_name = "blog_".blog_id;
            }

        	 jQuery.ajax({
	                type: "POST",
	                url: the_url,
	                data:({button_id:blog_id}),
	                success:function(data){
	                	retval=data.replace("n/a");
	                	openPopup(the_name, retval);
	        	 	}
	        });
        }

        function openPopup(name, url) {
        	window.open(url,name,"location=1,status=1,scrollbars=yes, width=650,height=550,top="+(screen.availHeight/2 - 300)+",left="+(screen.availWidth/2 - 300));
        }

img.script:
            $(window).load(function(){
            	var iframe = $('iframe.goog-te-menu-frame').contents();
                iframe.keydown(function(e){
                	if (e.keyCode == 40) { // down arrow get first link
                		e.preventDefault();
	                    e.stopPropagation();
	                    $(this).find('a:first').focus();
                	}
                });
                var menu = iframe.find('a').keydown(function(e){
                	var index = menu.index($(this));
                	if (e.keyCode == 40) { // down arrow
						e.preventDefault();
	                    e.stopPropagation();
						menu.eq(index + 1).focus();
					}
					if (e.keyCode == 38 && index > 0) { // up arrow
						e.preventDefault();
	                    e.stopPropagation();
						menu.eq(index - 1).focus();
					}
                });

            });

