    var IsMyViewPage = false;
	var ChannelBarIsSticky = false;

	$(window).load(function(){
		searchText();
		calendarListView();
	});

    $(window).scroll(function(e){
		stickyHeader();
	});

	$(function(){
    	// MYVIEW PAGE CHECK
		if($("#pw-body").length) IsMyViewPage = true;

