    function stickyHeader() {
        if(!IsMyViewPage) {
 			if($(window).width() >= 1280) { 
                var channelBarOffSet = $("main").offset().top
                if ($(window).scrollTop() <= channelBarOffSet) {
                    if(ChannelBarIsSticky) {
          	  			$("#gb-page").css("padding", "0"); 
              			$("#gb-header-outer").hide().removeClass("fixed-header").fadeIn(500);

                        ChannelBarIsSticky = false;
                    }

                    if($(".sp").length && $(".ui-widget.app.calendar").length) {
                        $(".wcm-controls").css("margin-top", "0px");
                    }
                } else {
                    if(!ChannelBarIsSticky) {
                        $("#gb-header-outer").hide().addClass("fixed-header");
                        $("#gb-page").css("padding", $("#gb-header-outer").height()+"px 0 0 0");
                        $("#gb-header-outer").fadeIn(500);

                        ChannelBarIsSticky = true;
                    }

                    if($(".sp").length && $(".ui-widget.app.calendar").length) {
                        if($(".wcm-controls").hasClass("wcm-stuck")) {
                            $(".wcm-controls").css("margin-top", $("#gb-channel-list-outer").outerHeight(true));
                        }
                    }
                }

                $("#sw-maincontent").css({
                    "display": "block",
                    "position": "relative",
                    "top": "-" + $("#gb-channel-list-outer").outerHeight(true) + "px"
                });
           } else if($(window).width() <= 768) { 
                if(ChannelBarIsSticky) {
          	  		$("#gb-page").css("padding", "0"); 
              		$("#gb-header-outer").hide().removeClass("fixed-header").fadeIn(500);

                    ChannelBarIsSticky = false;
                }

                if($(".sp").length && $(".ui-widget.app.calendar").length) {
                    $(".wcm-controls").css("margin-top", "0px");
                }
                $("#sw-maincontent").removeAttr("style");
            }
        }
    }

	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { //Desktop View

		} else{ //Mobile BPs
			if(getWidth <= 640) {
				appView();	
			}
		}
	}

	function modEvents() {
	    if (!$(".hp .upcomingevents .joel-month").length) {
	        $(".hp .upcomingevents").modEvents({
	            columns     : "no",
	            monthLong   : "no",
	            dayWeek     : "yes"
	        });
            $(".hp .upcomingevents .joel-date").each(function(){
				var text = $(this).text();
                if(text.length == 1) {
                	$(this).text("0"+text);
                }
            });
            $(".hp .upcomingevents .ui-article-description").each(function(){
	            $(".sw-calendar-block-title", this).prependTo($(this));
                if(!$(this).siblings(".sw-calendar-block-date").length) {
                	var parent = $(this).parent().parent().prev().find(".sw-calendar-block-date").clone();
                    $(this).before(parent);
                }
                if(!$(".sw-calendar-block-time", this).length) {
                	$(this).append('View Event');
                } else {
                    var text = $(".sw-calendar-block-time", this).text();
                    text = text.replace(" - ", "-");
                    text = text.replace(/ AM/g, "am");
                    text = text.replace(/ PM/g, "pm");
                    $(".sw-calendar-block-time", this).text(text);
                    $(".sw-calendar-block-time", this).append('View Event');
            	}
            });
            $(".hp.row.one .upcomingevents").each(function(){
            	var maxHeight = 211;
                $("li", this).each(function(){
                    if($(this).height() > maxHeight) {
                    	maxHeight = $(this).height();
                    }
                });              
                $("li .ui-article", this).height(maxHeight);
            });
            $(".hp.row.three .upcomingevents").each(function(){
            	var maxHeight = 211;
                $("li", this).each(function(){
                    if($(this).height() > maxHeight) {
                    	maxHeight = $(this).height();
                    }
                });              
                $("li .ui-article", this).height(maxHeight);
            });
	    }
	}

	function eventsByDay(container) {
	    var countLi = 0;
	    $(".ui-article", container).each(function(){
	        if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
	            var moveArticle = $(this).html();
	            $(this).parent().prev().children().children().next().append(moveArticle);
	            $(this).parent().remove();
	        };
	    });
	}

	function appView(){
		//DOC dont add app into accordion if it doesnt have a header
		$("#hp-content div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			} else{
				if( $('.ui-widget-header h1 .accordion-handle', this).length <= 0){
					$('.ui-widget-header h1', this).append('');
				}
			}
            if(!$("#hp-content div.ui-widget.app.active").length) {
            	$("#hp-content div.ui-widget.app").eq(0).addClass("active");
            }
		});

		//DOC accordion header click
		$("#hp-content div.ui-widget-header").off('click').click(function(){
			var clickedApp = $(this).parent().parent().attr("id");
