	function unStick(){
		$("#gb-page").removeClass("sticky");
		$("#gb-page").css({"margin-top":0});	
	}	

	function stickyChannels() {
		$(window).scroll(function() {
			//GET TOTAL HEIGHT
			var tH = $("#gb-alert-outer").height() + $("#gb-poplinks-outer").height() + $("#gb-header-outer").height(),
			    cH = $("#gb-channel-list-outer").height();
			if ($(window).scrollTop() > tH) {

                if($(".wcm-controls").hasClass("wcm-stuck")) {
                    $(".wcm-controls").css("margin-top", $("#gb-channel-list-outer").outerHeight(true));
                }
				$("#gb-page").addClass("sticky");
				$("#gb-page").css({"margin-top":cH+"px"});
            } else {
            	if($(".spn").length || $(".sp").length && $(".ui-widget.app.calendar").length) {
                    $(".wcm-controls").css("margin-top", "0px");
                }
				unStick();
			}
		});
	}	

	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			if(!$(".rs-gallery-container").length) { photoGallery(); }
			stickyChannels();

            if($("#sw-content-container1.cloned").length){
                $("#sw-content-container1").removeClass("cloned").prependTo("#hp-content .hp.column.one");
            }
            if($("#sw-content-container3.cloned").length){
                $("#sw-content-container3").removeClass("cloned").appendTo("#hp-content .hp.column.three");
            }

		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			if(!$(".rs-gallery-container").length) { photoGallery(); }
			appView768();
			unStick();
            if($("#sw-content-container3.cloned").length){
                $("#sw-content-container3").removeClass("cloned").appendTo("#hp-content .hp.column.three");
            }
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			if(!$(".rs-gallery-container").length) { photoGallery(); }
			appView();
			unStick();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			if(!$(".rs-gallery-container").length) { photoGallery(); }
			appView();
			unStick();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			if(!$(".rs-gallery-container").length) { photoGallery(); }
			appView();
			unStick();
		}
	}

