    function dropdownKeyboardFunctionality(dropdown, buttonParent){
        $('a', dropdown).first().trigger('focus');

        $('a', dropdown).off('keydown').keydown(function(e){
            var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };
            var linkObj = $('a', dropdown);

            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                        // IS FIRST ITEM
                    if(linkObj.index($(this)) == 0) {
                        buttonParent.focus();
                    } else {
                        linkObj[linkObj.index($(this)) - 1].focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if(linkObj.index($(this)) == (linkObj.length - 1) ) {
                        // FOCUS FIRST ITEM
                        linkObj[0].focus();
                    } else {
                        // FOCUS NEXT ITEM
                        linkObj[linkObj.index($(this)) + 1].focus();
                    }
                break;

                case csKeyCodes.tab:
                case csKeyCodes.esc:
                	e.preventDefault();
                    e.stopImmediatePropagation();
                    buttonParent.click().focus();
                break;

                default: //pass
                break;
            }
        });
    }

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
