Departments");
		}
        }

	function dynamicContent(){
		// ********** MYSTART ********** //
		//DOC replace mystart dropdown arrows
		$("#sw-mystart-outer").prependTo("#gb-header-outer");
		$('#gb-global-icons').before($("#sw-mystart-search"));
		$(".sw-mystart-dropdown.schoollist .selector, .sw-mystart-dropdown.schoollist .sw-dropdown-selected").text("Choose a School");
		$(".sw-mystart-button.pop-links").prependTo("#sw-mystart-right").removeClass("hidden");
		$(".sw-mystart-button.pop-links").on("click", function(){
            if($(this).attr('aria-expanded') == 'false'){
            	$("#gb-poplinks").removeClass("hidden");
            	$(this)
                	.attr('aria-expanded', true)
                	.addClass('active');
                dropdownKeyboardFunctionality($('#gb-poplinks'), $(this)); 

            }else{
            	$("#gb-poplinks").addClass("hidden");
            	$(this)
                	.attr('aria-expanded', false)
                	.removeClass('active');
            }
		})
        .on('keydown', function(e){
        	if(e.keyCode == 37 || e.keyCode == 38){
            	e.preventDefault();
                dropdownKeyboardFunctionality($('#gb-poplinks'), $(this));
            }
        });



		// ********** HEADER ********** //
        //Hide Sitename and Tagline if flag is set to false
		//var showSitename = 'false';
        //if(showSitename == "false") {
        //	$("#sitename").hide();
