      targetView = $('#hid-'+MIID+'-DetailView').val();
   }
  GetContent('https://www.cpsk12.org//cms/UserControls/ModuleView/ModuleViewRendererWrapper.aspx?DomainID=9415&PageID=11993&ModuleInstanceID=' + MIID + '&PageModuleInstanceID=' + PMI + '&FlexDataID=' + flexDataID + '&GroupYear=' + groupYear + '&GroupMonth=' + groupMonth + '&GroupByField=' + groupBy + '&RenderLoc=0&FromRenderLoc=0&EnableQuirksMode=0&Tag=' + tag + '&ViewID=' + targetView, container, 2, 'chkSidebar();');
}
