   $(document).ready(function(){
      var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=0razu1TPtH2m9HqEXfvWkA%3d%3d&SessionID=f15cd39c-ebda-4cde-890f-b592dc830fb0&SiteID=4&ChannelID=40&SectionID=9415&PageID=11993&HitDate=4%2f29%2f2020+12%3a03%3a48+PM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.77.15';
      try {
         $.getJSON(beaconURL + '&jsonp=?', function(myData) {});
      } catch(err) { 
         // prevent site error for analytics
      }
