                for (var $i = 1; $i <= 12; $i++) {
                    ($('.masterC.alignment-stretch  .contentsection:nth-child(' + $i + ')').css({ "z-index": (10 * $i) }));
                }


                var exist = jQuery('#backtotop').length;
                if (exist == 0) {
                    $("body").append("
Back to Top");
                }

                $(window).scroll(function () {
                    var scrollPosition = $(this).scrollTop();
                    if (scrollPosition >= 200) {
                        $('#backtotop').fadeIn();
                        $('#a.logo').fadeIn();
                        ($('.head-logo-container').css({ "width": "100%", "position": "fixed" }));
                        ($('.header-elements').css({ "bottom": "0", "position": "relative" }));
                        ($('a.logo').css({ "display": "none" }));
                        ($('.NavBack').css({ "position": "fixed" }));

                    } else {
                        $('#backtotop').fadeOut();
                        $('#a.logo').fadeOut();
                        ($('.head-logo-container').css({ "width": "100%", "position": "relative" }));
                        ($('.header-elements').css({ "bottom": "30px", "position": "absolute" }));
                        ($('a.logo').css({ "display": "block" }));
                        ($('.NavBack').css({ "position": "absolute" }));
                    }

                });

                $('#backtotop').click(function () {
                    $("html, body").bind("scroll mousedown DOMMouseScroll mousewheel keyup", function () {
                        $('html, body').stop();
                    });
                    $('html,body').animate({ scrollTop: 0 }, 1200, 'easeOutQuart', function () {
                        $("html, body").unbind("scroll mousedown DOMMouseScroll mousewheel keyup");
                    });
                    return false;
                });
            });

            $(window).resize(function () {
                ($('img.school-image').css({ "height": $('img.school-image').width() + "px" }));
            });
