        },

        "Slideshow": function() {
            // FOR SCOPE
            var _this = this;

            //SHOW SLIDESHOW OVERLAY REGION IF THERE IS AN APP THERE
            if($("#hp-top-content-wrapper .app").length){
                //$("#hp-top-content-wrapper").show();
