            if(!$("#sw-content-container10.region.ui-hp .rs-photo-gallery-container").length){
                if($(".sw-special-mode-bar").length && !$("#sw-content-container10.ui-hp div.ui-widget.app.gallery.json").length) {
                    $("#sw-content-container10.ui-hp").html("


var photoGallery0000={'Album':{'title': 'Photo Gallery','description': '','transitiondelay': '6','fadestyle': 'NO','gallerywidth': '1070','backgroundaudio': '','loopaudio': 'False','lgpath': '','tnpath': '','Images': [{'photoname': 'Photography Showcase','src': '/cms/lib/MT15000302/Centricity/Template/16/default/slideshow-default.jpg','caption': 'This is the photo caption text that describes the photo in more detail','pause': '','link': '','target': ''}]}}</" + "scr" + "ipt>");
                }

                if('Small Photo Gallery' == "Large Photo Gallery"){
                    var pgImageWidth = 1520;
                    var pgImageHeight = 656;
                } else {
                    var pgImageWidth = 760;
                    var pgImageHeight = 440;
                }
                // PHOTO GALLERY ROTATOR
                $("#sw-content-container10.ui-hp div.ui-widget.app.gallery.json").creativeGalleryRotate({
                          "imageWidth"                : pgImageWidth,
                          "imageHeight"               : pgImageHeight,
                          "efficientLoad"             : "yes",
                          "appHeader"                 : "no",
                          "titles"                    : "yes",
