        "ModEvents": function() {
            $(".ui-widget.app.upcomingevents").modEvents({
                columns     : "yes",
                monthLong   : "no",
                dayWeek     : "yes"
            });

            eventsByDay(".upcomingevents .ui-articles");

            function eventsByDay(container) {
                $(".ui-article", container).each(function(){
                    if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                        var moveArticle = $(this).html();
                        $(this).parent().prev().children().children().next().append(moveArticle);
                        $(this).parent().remove();
                    };

                    //MOVE DAY INTO RIGHT COLUMN
                    var eventDay = $(this).find(".joel-day").text();
                    $(this).find('.upcoming-column.right .ui-article-description').prepend(eventDay+", ");
                }); 

                $(".ui-article", container).each(function(){
                    var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
                    var newDate = '';
                    var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

                    //add a zero to the date if it is a single digit
                    if( newDateTime[dateIndex].length == 1 ) newDateTime[dateIndex] = "0" + newDateTime[dateIndex];

                    //if we have the day of the week make sure to include it.
                    if( dateIndex > 1 ){ 
                        newDate += newDateTime[0] + "";
                    }

                    //add in the month
                    newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

                    //wrap the date in a new tag
                    newDate += '' + newDateTime[dateIndex] + '';

                    //append the date and month back into their columns
                    $('.upcoming-column.left h1', this).html(newDate);  


                    //add an ALL DAY label if no time was given
                    $('.upcoming-column.right .ui-article-description', this).each(function(){
                        if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
                            $(this).append('ALL DAY');
                        }
                    });

                    	var eventMonth = $(this).find(".joel-month").text().toLowerCase();
                        var eventNumberDay = $(this).find(".jeremy-date").text();

                        //var eventDetails = $(this).find(".upcoming-column.right").html();
                        var ariaMonth = "";
                        var ariaDate = "";
                        switch(eventMonth){
                          case "jan":
                            ariaMonth = "January";
                          break;
                          case "feb":
                            ariaMonth = "February";
                          break;
                          case "mar":
                            ariaMonth = "March";
                          break;
                          case "apr":
                            ariaMonth = "April";
                          break;
                          case "may":
                            ariaMonth = "May";
                          break;
                          case "jun":
                            ariaMonth = "June";
                          break;
                          case "jul":
                            ariaMonth = "July";
                          break;
                          case "aug":
                            ariaMonth = "August";
                          break;
                          case "sep":
                            ariaMonth = "September";
                          break;
                          case "oct":
                            ariaMonth = "October";
                          break;
                          case "nov":
                            ariaMonth = "November";
                          break;
                          case "dec":
                            ariaMonth = "December";
                          break;
                          default: ariaMonth = eventMonth;
                        }
                        switch(eventNumberDay){
                          case "01":
                            ariaDate = "First";
                          break;
                          case "02":
                            ariaDate = "Second";
                          break;
                          case "03":
                            ariaDate = "Third";
                          break;
                          case "04":
                            ariaDate = "Fourth";
                          break;
                          case "05":
                            ariaDate = "Fifth";
                          break;
                          case "06":
                            ariaDate = "Sixth";
                          break;
                          case "07":
                            ariaDate = "Seventh";
                          break;
                          case "08":
                            ariaDate = "Eighth";
                          break;
                          case "09":
                            ariaDate = "Ninth";
                          break;
                          case "10":
                            ariaDate = "Tenth";
                          break;
                          case "11":
                            ariaDate = "Eleventh";
                          break;
                          case "12":
                            ariaDate = "Twelveth";
                          break;
                          case "13":
                            ariaDate = "Thirteenth";
                          break;
                          case "14":
                            ariaDate = "Fourteenth";
                          break;
                          case "15":
                            ariaDate = "Fifteenth";
                          break;
                          case "16":
                            ariaDate = "Sixteenth";
                          break;
                          case "17":
                            ariaDate = "Seventeenth";
                          break;
                          case "18":
                            ariaDate = "Eighteenth";
                          break;
                          case "19":
                            ariaDate = "Nineteenth";
                          break;
                          case "20":
                            ariaDate = "Twentieth";
                          break;
                          case "21":
                            ariaDate = "Twenty-first";
                          break;
                          case "22":
                            ariaDate = "Twenty-second";
                          break;
                          case "23":
                            ariaDate = "Twenty-third";
                          break;
                          case "24":
                            ariaDate = "Twenty-fourth";
                          break;
                          case "25":
                            ariaDate = "Twenty-fifth";
                          break;
                          case "26":
                            ariaDate = "Twenty-sixth";
                          break;
                          case "27":
                            ariaDate = "Twenty-seventh";
                          break;
                          case "28":
                            ariaDate = "Twenty-eighth";
                          break;
                          case "29":
                            ariaDate = "Twenty-ninth";
                          break;
                          case "30":
                            ariaDate = "Thirtieth";
                          break;
                          case "31":
                            ariaDate = "Thirty-first";
                          break;
                          default: ariaDate = eventNumberDay;
                        }

                        $(this).find('.sw-calendar-block-date').attr('aria-label', ariaMonth+" "+ariaDate);

                })


            }

            //EVENT HOVERS
            $(".sw-calendar-block-title").hover(function(){
            	$(this).parentsUntil("li").addClass("hover");
            }, function(){
            	$(this).parentsUntil("li").removeClass("hover");
            })

            $(".sw-calendar-block-title a").focus(function(){
            	$(this).parentsUntil("li").addClass("hover");
            })

            $(".sw-calendar-block-title a").focusout(function(){
            	$(this).parentsUntil("li").removeClass("hover");
            })

            $('.upcomingevents').each(function() {
               if(!$('.ui-article', this).length) {
                   $('.ui-articles',this).html('




There are no upcoming events to display.');
               }
            });

            $(".ui-article-title.sw-calendar-block-date span").attr("aria-hidden","true");
        },

