body.script:
;(function($) { 'use strict';var hostname = document.location.hostname, installFolder = $.trim($('#installFolder').text()), socsPage = 'article';
  function commonChecks() {

    // If something bad happened with the install folder session variable
    if (installFolder.length === 0) return false;

    // If the hostname ends with "socs.net" or "fes.org" - no updating needed
    if (hostname.match(/socs\.net$/) !== null || hostname.match(/fes\.org$/) !== null) return false;

    return true;
  }

  function updateStaffLinks() {

    if ( ! commonChecks()) return false;

    if (hostname != installFolder) {
      $('a[href*="email-friend.v?TARGET=staff"]').each(function() {
        $(this).attr('href', '//' + installFolder + $(this).attr('href'));
      });
    }
  }

  function updateURL() {
    var updatedHref = '';

    if ( ! commonChecks()) return false;

    // If the hostname in the page's URL does not match the install folder - update the URL and reload the page
    if (hostname != installFolder) {
      updatedHref = location.href.replace(hostname, installFolder);
      location.href = updatedHref;
    }
  }

  // Is a formmail form in the page that includes Google's reCAPTCHA?  If so, reload the page with an updated URL if necessary
  if ($('form[action$="/formmail/formmail.php"] .g-recaptcha').length) updateURL();

  // If on the "Sign Up" page for public users, reload the page with an updated URL if necessary
  if (socsPage === 'register_form' && $('form button.g-recaptcha').length) updateURL();

  $(function() {

    // Staff Page section page email links
    if ($('a[href*="email-friend.v?TARGET=staff"]').length) updateStaffLinks();

  });
})(jQuery);

;(function($) { 'use strict';
  var socsPage = 'article', scrollToContent = '';


  // Pirobox code - only exists within an article
  if (socsPage === 'article') {
    $().piroBox({
      overlay_color: '#000000',
      my_speed: 400,
      close_speed: 500,
      bg_alpha: 0.3,
      slideShow : true,
      slideSpeed : 5,
      close_all : '.piro_close,.piro_overlay'
    });
  }

  if (scrollToContent && socsPage != 'main') {
    // Wait until the images in the rotator have loaded
    $('.container-rotator').imagesLoaded(function() {
      $('html, body').animate({
        scrollTop: $('.container-body').offset().top
      }, 'slow');
    });
  }

  // Clone vbuttons and add them in the multiple places they are used
  if ($('.masthead-style-1 .vbuttons').length) {
    $('.masthead-style-1 .vbuttons').clone().prependTo('.navbar-header .ns2-menu-wrapper');
  }




  // If there are vbuttons inside the masthead container (non-mobile), clone them and add them to the navbar (mobile)
  if ($('.container-masthead .ns2-menu-wrapper .dropdown.vbuttons').length) {
    $('.container-masthead .ns2-menu-wrapper .dropdown.vbuttons').clone().appendTo('.navbar-header .ns2-menu-wrapper');
  }

  // Support for submenus - https://github.com/vsn4ik/bootstrap-submenu
  $('.dropdown-submenu > a').submenupicker();

  // Bootstrap bug? - Clicking a 1st-level section does nothing but toggle the dropdown menu - so force it to go to the href
  $('.container-nav .navbar-nav > .dropdown > a:visible').on('click', function() {
    var $this = $(this), href = $this.attr('href');
    if ($this.attr('target') === '_blank') {
      window.open(href, '_blank');
    } else {
      window.location = href;
    }
  });



})(jQuery);
