			/* If a Gravity Form, we can use custom tracking */
			if ( exactmetrics_form_id && 0 === exactmetrics_form_id.lastIndexOf( 'gform_', 0 ) ) {
				continue;
			}

			/* If Ninja forms, we use custom conversion tracking */
			if ( skip_conversion ) {
