	function jsMediaQueries() {
		switch(csGetBreakPoint()) {
			case "desktop": 
				headerMods();
                moveSearchLinksBack();
			break;
			case "768":
				hpAppCopy();
                moveSearchLinks();
			break;
			case "640":
				hpAppCopy();
                moveSearchLinks();
			break;
			case "480":
				hpAppCopy();
                moveSearchLinks();
			break;
			case "320":
				hpAppCopy();
                moveSearchLinks();
			break;
		}
	}

    function moveSearchLinks(){

        if(!$("#gb-channel-list-outer #gb-search-links").length){
        	$("#gb-search-links").appendTo("#gb-channel-list-outer").show();
        }

    }

    function moveSearchLinksBack(){

        if(!$("#header-right #gb-search-links").length){
        	$("#gb-search-links").appendTo("#header-right").show();
        } else {
        	$("#gb-search-links").show();
        }

    }

	function hpAppCopy(){
		//ONLY CLONE APPS IF IT HASNT HAPPENED ALREADY
		if(!$("#mobile-app-container .app").length){
			//CLONE ALL APPS FROM CONTAINER 3 EXCEPT FOR HEADLINES AND PLACE THEM IN THE MOBILE APP CONTAINER
			$("#sw-content-container3 div.ui-widget.app").not("div.ui-widget.app.headlines").each(function(){
				$(this).clone().appendTo("#mobile-app-container");
			});
		}

	}

	function hpSocialAccordion(){
		if($(".has-social-feeds").length){
			if(!$(".has-social-feeds").hasClass("app-accordion")){
				$(".has-social-feeds").addClass("app-accordion");
			}
            $(".ac-widget-header").click(function(){
                if($(".app-accordion").hasClass("open")){
                	$(".app-accordion").removeClass("open");
                } else {
                	$(".app-accordion").addClass("open");
                }
            });
		}
	}

	function headerMods(){
		//SET HEADER HEIGHT
		var wh = $(window).height();
		$(".hp header").height(wh);

		$(document).scroll(function() {
			var distanceFromTop = $(this).scrollTop();

			if($(".hp").length){
            	if(distanceFromTop >= 50){
                	$("nav").removeClass("on-bottom");
                } else {
                	$("nav").addClass("on-bottom");
                }
				if(distanceFromTop >= 135){
					$("#gb-page").addClass("sticky-mystart");
				} else {
					$("#gb-page").removeClass("sticky-mystart");
				}

				var channelStickScroll = wh - 109;

				if(distanceFromTop >= channelStickScroll){
					$("#gb-page").addClass("sticky-channels");
				} else {
					$("#gb-page").removeClass("sticky-channels");
				}

                $("#sw-maincontent").css({
                    "display": "block",
                    "position": "relative",
                    "top": "-" + $("#gb-channel-list-outer").outerHeight(true) + "px"
                });
			} else {
				if(distanceFromTop >= 135){
					$("#gb-page").addClass("sticky-mystart sticky-channels-sp");
                    if($(".sp").length && $(".ui-widget.app.calendar").length) {
                        if($(".wcm-controls").hasClass("wcm-stuck")) {
                            $(".wcm-controls").css("margin-top", $("#gb-channel-list-outer").outerHeight(true) + $("#gb-mystart").outerHeight(true));
                        }
                    }
				} else {
					$("#gb-page").removeClass("sticky-mystart sticky-channels-sp");
                    if($(".sp").length && $(".ui-widget.app.calendar").length) {
                        if($(".wcm-controls").hasClass("wcm-stuck")) {
                            $(".wcm-controls").css("margin-top","0px");
                        }
                    }
				}
			}

		});
	}

	function specialAnnouncement(){
		if($("#alert-app .ui-articles li").length){
			$("#alert-app").show();			
		}

	}

	function navLine(){
		var $element, leftPos, newWidth,
        $mainNav = $("#channel-navigation");

        if(!$("#nav-line").length){
        	$mainNav.append("
");
        }


	    var $navLine = $("#nav-line");

	    $navLine
	        .width($(".sw-channel-item.active").width())
	        .css("left", $(".sw-channel-item.active").position().left)
	        .data("origLeft", $navLine.position().left)
	        .data("origWidth", $navLine.width());

	    $("#channel-navigation > li").hover(function() {
	        $element = $(this);
	        leftPos = $element.position().left;
	        newWidth = $element.width();
	        $navLine.stop().animate({
	            left: leftPos,
	            width: newWidth
	        });
	    }, function() {
	        $navLine.stop().animate({
	            left: $navLine.data("origLeft"),
	            width: $navLine.data("origWidth")
	        });    
	    });
	}

	function accessibilityFunctions(){
        $("#sw-search-button").attr("aria-label", "Click to search");



		$("#sw-mystart-mypasskey").attr({
			"aria-label":	"Open My Passkey",
			"role":		"button"
		});

		// KEY MAPPING
		var csKeyCodes = { "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40, "tab": 9 };


		$(".sw-mystart-dropdown.translate .selector").keydown(function(e) {
			// CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.enter:
				case csKeyCodes.space:
					e.preventDefault();

					//IF THE DROPDOWN IS OPEN, CLOSE IT
					if($(".sw-mystart-dropdown.translate").hasClass("open")){
						$(".sw-mystart-dropdown.translate .sw-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".sw-mystart-dropdown.translate").removeClass("open"); 
					} else {
						$(".sw-mystart-dropdown.translate .sw-dropdown").attr("aria-hidden","false").slideDown(300, "swing", function(){

							setTimeout(function(){
								$(".sw-dropdown", this).focus();
							}, 100);
						});
						$(".sw-mystart-dropdown.translate").addClass("open");
					}



				break;

				// CONSUME TAB KEY
				case csKeyCodes.tab:
					if(e.shiftKey){
						$(".sw-mystart-dropdown.translate .sw-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".sw-mystart-dropdown.translate").removeClass("open");
					} else {
						e.preventDefault();
						$(".sw-mystart-dropdown.translate .sw-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".sw-mystart-dropdown.translate").removeClass("open");
						$(".sw-mystart-button.home a").focus();
					}

				break;

				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.down:
				case csKeyCodes.right:
					e.preventDefault();

					$(".sw-mystart-dropdown.translate .sw-dropdown-list li:first-child > a").focus();

				break;
			}
		});

		$(".sw-mystart-dropdown.translate li a").keydown(function(e) {
			// CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.left:
				case csKeyCodes.up:
					e.preventDefault();

					// IS FIRST ITEM
					if($(this).parent().is(":first-child")) {
						// FOCUS DROPDOWN BUTTON
						$(".sw-mystart-dropdown.translate .selector").focus();
					} else {
						// FOCUS PREVIOUS ITEM
						$(this).parent().prev("li").find("> a").focus();
					}
				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case csKeyCodes.right:
				case csKeyCodes.down:
					e.preventDefault();

					// IS LAST ITEM
					if($(this).parent().is(":last-child")) {
						// FOCUS FIRST ITEM
						$(".sw-mystart-dropdown.translate .sw-dropdown-list li:first-child > a").focus();
					} else {
						// FOCUS NEXT ITEM

						$(this).parent().next("li").find("> a").focus();
					}
				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM
					$(".sw-mystart-dropdown.translate .sw-dropdown-list li:first-child > a").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM
					$(".sw-mystart-dropdown.translate .sw-dropdown-list li:last-child > a").focus();
				break;

			}
		});


		$(".ac-dropdown-container.global-links .selector").keydown(function(e) {
			// CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.enter:
				case csKeyCodes.space:
					e.preventDefault();

					//IF THE DROPDOWN IS OPEN, CLOSE IT
					if($(".ac-dropdown-container.global-links").hasClass("open")){
						$(".ac-dropdown-container.global-links .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container.global-links").removeClass("open"); 
					} else {
						$(".ac-dropdown-container.global-links .ac-dropdown").attr("aria-hidden","false").slideDown(300, "swing", function(){

							setTimeout(function(){
								$(".ac-dropdown", this).focus();
							}, 100);
						});
						$(".ac-dropdown-container.global-links").addClass("open");
					}



				break;

				// CONSUME TAB KEY
				case csKeyCodes.tab:
					if(e.shiftKey){
						$(".ac-dropdown-container.global-links .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container.global-links").removeClass("open");
					} else {
						e.preventDefault();
						$(".ac-dropdown-container.global-links .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container.global-links").removeClass("open");
						$(".sw-mystart-dropdown.translate .selector").focus();
					}

				break;

				// CONSUME DOWN AND RIGHT ARROWS
				case csKeyCodes.down:
				case csKeyCodes.right:
					e.preventDefault();

					$(".ac-dropdown-container.global-links li:first a").focus();

				break;
			}
		});


		$(".ac-dropdown-container.global-links li a").keydown(function(e) {

            // CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.left:
				case csKeyCodes.up:
					e.preventDefault();

					// IS FIRST ITEM
					if($(this).parent().is(":first-child")) {
						// FOCUS DROPDOWN BUTTON
						$(".ac-dropdown-container.global-links .selector").focus();
					} else {
						// FOCUS PREVIOUS ITEM
						$(this).parent().prev("li").find("> a").focus();
					}
				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case csKeyCodes.right:
				case csKeyCodes.down:
					e.preventDefault();

					// IS LAST ITEM
					if($(this).parent().is(":last-child")) {
						// FOCUS FIRST ITEM
						$(".ac-dropdown-container.global-links .ac-dropdown-list li:first-child > a").focus();
					} else {
						// FOCUS NEXT ITEM
						$(this).parent().next("li").find("> a").focus();
					}
				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM
					$(".ac-dropdown-container.global-links .ac-dropdown-list li:first-child > a").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM
					$(".ac-dropdown-container.global-links .ac-dropdown-list li:last-child > a").focus();
				break;

			}
		});

        $(".ac-dropdown-container.school-list .selector").keydown(function(e) {
			// CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.enter:
				case csKeyCodes.space:
					e.preventDefault();

					//IF THE DROPDOWN IS OPEN, CLOSE IT
					if($(".ac-dropdown-container.school-list").hasClass("open")){
						$(".ac-dropdown-container.school-list .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container.school-list").removeClass("open"); 
					} else {
						$(".ac-dropdown-container.school-list .ac-dropdown").attr("aria-hidden","false").slideDown(300, "swing", function(){

							setTimeout(function(){
								$(".ac-dropdown", this).focus();
							}, 100);
						});
						$(".ac-dropdown-container.school-list").addClass("open");
					}



				break;

				// CONSUME TAB KEY
				case csKeyCodes.tab:
					if(e.shiftKey){
						$(".ac-dropdown-container.school-list .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container.school-list").removeClass("open");
					} else {
						e.preventDefault();
						$(".ac-dropdown-container.school-list .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container.school-list").removeClass("open");
						$(".text-link.families").focus();
					}

				break;

				// CONSUME DOWN AND RIGHT ARROWS
				case csKeyCodes.down:
				case csKeyCodes.right:
					e.preventDefault();

					$(".ac-dropdown-container.school-list li:first a").focus();

				break;
			}
		});

		$(".ac-dropdown-container.school-list li a").keydown(function(e) {

            // CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.left:
				case csKeyCodes.up:
					e.preventDefault();

					// IS FIRST ITEM
					if($(this).parent().is(":first-child")) {
						// FOCUS DROPDOWN BUTTON
						$(".ac-dropdown-container.school-list .selector").focus();
					} else {
						// FOCUS PREVIOUS ITEM
						$(this).parent().prev("li").find("> a").focus();
					}
				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case csKeyCodes.right:
				case csKeyCodes.down:
					e.preventDefault();

					// IS LAST ITEM
					if($(this).parent().is(":last-child")) {
						// FOCUS FIRST ITEM
						$(".ac-dropdown-container.school-list .ac-dropdown-list li:first-child > a").focus();
					} else {
						// FOCUS NEXT ITEM
						$(this).parent().next("li").find("> a").focus();
					}
				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM
					$(".ac-dropdown-container.school-list .ac-dropdown-list li:first-child > a").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM
					$(".ac-dropdown-container.school-list .ac-dropdown-list li:last-child > a").focus();
				break;

			}
		});

		$(".social-header.twitter").keydown(function(e) {
			// CAPTURE KEY CODE
			switch(e.keyCode) {
				case csKeyCodes.tab:
					if(e.shiftKey){

					} else {
