	function fixGlobals(){
		//GIVE GLOBALS PROPER SYNTAX FOR DROPDOWN
		$(".global-icon").not(".blackboard-link .global-icon").each(function(){
			$(".icon", this).appendTo(this);
            if(!$(this).parent().is("li")){
            	$(this).wrap("
");
            }

		});

		//PLACE BLACKBOARD LINKS AT THE BOTTOM OF THE LIST
		$(".ac-dropdown-container.global-links").each(function(){
			$("li", this).each(function(){
				if($(this).hasClass("blackboard-link")){
					$(this).appendTo($(this).parent());
				}
			});
		});


	}

	function socialTabs(){

		var showFeeds = 'yes';

		if(showFeeds == "yes"){
			//SHOW THE HEADER
			$("#social-feed-header").show();

			//ADD CLASS TO CONTAINER FOR SOCIAL STYLING
			$("#hp-content-bottom .hp.column.two").addClass("has-social-feeds");

            //MOVE FEEDS INTO THEIR CONTAINERS
            $("#sw-content-container6 div.ui-widget.app").first().appendTo("#facebook-feed");
            $("#sw-content-container6 div.ui-widget.app").first().appendTo("#twitter-feed");



			//SHOW THE FIRST APP ON PAGE LOAD
			$(".social-header").click(function(){
				if(!$(this).hasClass("active")){
					$(".social-header, .feed-container").toggleClass("active");
                    $(this).attr("aria-expanded", "true");
                    $(".social-header").not(this).attr("aria-expanded", "false");

                    //TOGGLE A11Y ON FEED CONTAINERS
                    $(".feed-container.active").attr("aria-hidden", "false");
                    $(".feed-container").not(".active").attr("aria-hidden", "true");
				}

			});
		}
	}

	function modEvents(){
		head.ready("twoColumnEvents", function(){
			if (!$(".upcomingevents .joel-month").length) {
		        $(".upcomingevents").modEvents({
		            columns     : "yes",
		            monthLong   : "yes",
		            dayWeek     : "yes"
		        });
		        eventsByDay(".upcomingevents .ui-articles");
		    }
		});
	}

	function eventsByDay(container) {
	    var countLi = 0;
	    $(".ui-article", container).each(function(){
	        if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
	            var moveArticle = $(this).html();
	            $(this).parent().prev().children().children().next().append(moveArticle);
	            $(this).parent().remove();
	        };
	    }); 

	    $(".ui-article", container).each(function(){
			var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
			var newDate = '';
			var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

			//if we have the day of the week make sure to include it.
			if( dateIndex > 1 ){ 
				newDate += newDateTime[0] + "";
			}

			//add in the month
			newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

            //add a zero to the date if it is a single digit
			if( newDateTime[dateIndex].length == 1 ){
            	//wrap the date in a new tag
				newDate += '' + newDateTime[dateIndex] + '';
            } else {
            	//wrap the date in a new tag
				newDate += '' + newDateTime[dateIndex] + '';
            }

			//append the date and month back into their columns
			$('.upcoming-column.left h1', this).html(newDate);	


			//add an ALL DAY label if no time was given
			$('.upcoming-column.right .ui-article-description', this).each(function(){
				if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
					$(this).prepend('ALL DAY');
				}
			});

			//WRAP DAY NAME AND MONTH IN A CONTAINER
			$(".joel-day, .joel-month", this).wrapAll("");

		}); 



	}

