		                "galleryOverlay"          : "no",
		                "linkedElement"           : [""], // ["image", "title", "overlay", "all"]
		                "playPause"               : "no",
		                "mainControls"            : "yes",
		                "contMoreLink"            : "no",
		                "contMoreLinkText"        : "Read More",
		                "descMoreLink"            : "yes",
		                "descMoreLinkText"        : "MORE INFO",
		                "bullets"                 : "no",
		                "bulletControls"          : "no",
		                "thumbnails"              : "no",
		                "thumbnailWidth"          : 122,
		                "thumbnailHeight"         : 53,
		                "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
		                "hoverPause"              : "yes",
		                "autoRotate"              : "yes",
		                "fullScreenRotator"       : "yes", // image dimensions work well at 1500x915
		                "fullScreenBreakpoints"   : ["960"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
		                "fullScreenVideo"         : "no",
		                "transitionType"          : "slide", // fade or slide
		                "transitionSpeed"         : 3,
		                "transitionDelay"         : 8,
		                "onImageLoad"             : function() { },
