		                "allLoaded"               : function() { scrollDownBtn(); }
		        });
			}
		});
	}

	function scrollDownBtn(){
		$(".rs-gallery-control.next").after("SCROLL DOWN");

		var wh = $(window).height() - 109;

		$("#scroll-down").click(function(){
    		$('html, body').animate({
		        scrollTop: $("#hp-content-top").offset().top - 109
		    }, 500, function(){
		    	$(".hp.column.one a:first").focus();
		    });
    	});

        //ONLY SHOW CHANNEL BAR AFTER MMR HAS LOADED TO PREVENT OVERLAPPING WITH MYSTART
        $("nav").show();

	}

