            });

            // APPEND ALL HIDDEN CHANNELS BEFORE BEGINNING SO THAT THEY DON'T INTERFERE WITH INDEXES
            $("#channel-navigation").append($("#channel-navigation .hidden-channel"));

            var megaMenuChannels = [
