	$(function () {		

	$('.btnGetAll').click(function () {
	if ($('.chkNumber:checked').length) {

	var chkId = '';	

	$('.chkNumber:checked').each(function () {

	chkId += $(this).val() + ",";  });

	chkId = chkId.slice(0, -1);		

	document.getElementById('deleteid').value=chkId;

		if(confirm('Are you sure you would like to delete?')) {	

		var id = '#dialog';

		//Get the screen height and width
		var maskHeight = $(document).height();
		var maskWidth = $(window).width();

		//Set heigth and width to mask to fill up the whole screen
		$('#mask').css({'width':maskWidth,'height':maskHeight});

		//transition effect
		$('#mask').fadeIn(500);	
		$('#mask').fadeTo("slow",0.9);	

		//Get the window height and width
		var winH = $(window).height();
		var winW = $(window).width();

		//Set the popup window to center
		$(id).css('top',  winH/2-$(id).height()/2);
		$(id).css('left', winW/2-$(id).width()/2);

		//transition effect
		$(id).fadeIn(2000); 	

		//if close button is clicked
		$('.window .close').click(function (e) {
		//Cancel the link behavior
		e.preventDefault();

		$('#mask').hide();
		$('.window').hide();
		});

		//if mask is clicked
		$('#mask').click(function () {
		$(this).hide();
		$('.window').hide();
		});

		} 

		else { return false; }	

	}

	else { alert('No User Name Have Been Selected To Delete'); } });	

	$('.chkSelectAll').click(function () {

	$('.chkNumber').prop('checked', $(this).is(':checked')); });



	$('.chkNumber').click(function () {

	if ($('.chkNumber:checked').length == $('.chkNumber').length){

	$('.chkSelectAll').prop('checked', true); }

	else { $('.chkSelectAll').prop('checked', false); }

	});

});	

