	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery();
			$(window).scroll(parallaxScroll);
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery768();
			appView();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery768();
			appView();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			photoGallery768();
			appView();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			photoGallery768();
			appView();
		}
	}

	function photoGallery(){
		var videoToggle = 'false';
		if(videoToggle == "false") {
			if(!$("#sw-content-container11.region.ui-hp .rs-gallery-container").length){
			        // MULTIMEDIA ROTATOR
			        $("#sw-content-container11.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
			                "imageWidth"              : 1500,
			                "imageHeight"             : 915,
			                "appHeader"               : "no",
			                "titles"                  : "yes",
			                "captions"                : "yes",
			                "galleryOverlay"          : "yes",
			                "linkedElement"           : ["image"], // ["image", "title", "overlay", "all"]
			                "playPause"               : "no",
			                "mainControls"            : "no",
			                "contMoreLink"            : "no",
			                "contMoreLinkText"        : "Read More",
			                "descMoreLink"            : "no",
			                "descMoreLinkText"        : "Read More",
			                "bullets"                 : "no",
			                "bulletControls"          : "no",
			                "thumbnails"              : "no",
			                "thumbnailWidth"          : 122,
			                "thumbnailHeight"         : 53,
			                "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
			                "hoverPause"              : "yes",
			                "autoRotate"              : "yes",
			                "fullScreenRotator"       : "yes", // image dimensions work well at 1500x915
			                "fullScreenVideo"         : "no",
			                "transitionType"          : "fade", // fade or slide
			                "transitionSpeed"         : 3,
			                "transitionDelay"         : 8,
			                "onImageLoad"             : function() { 
			                				var getWidth = $(window).width();
									var contentWidth = $("#gb-channel-list").width();
									var offset = (getWidth - contentWidth)/2;
									$("#hp-slideshow .rs-gallery-container .rs-gallery-desc").css("left", offset);
									$(window).resize(function(){
										var getWidth = $(window).width();
										var contentWidth = $("#gb-channel-list").width();
										var offset = (getWidth - contentWidth)/2;
										$("#hp-slideshow .rs-gallery-container .rs-gallery-desc").css("left", offset);	
									});
			                },
			                "allImagesLoaded"         : function() { },
			                "onThumbnailLoad"         : function() { },
			                "allThumbnailsLoaded"     : function() { },
			                "onTransitionStart"       : function() { },
			                "onTransitionEnd"         : function() { },
			                "watchVidClick"           : function() { },
			                "closeVidClick"           : function() { },
			                "allLoaded"               : function() { 
			                				var getWidth = $(window).width();
									var contentWidth = $("#gb-channel-list").width();
									var offset = (getWidth - contentWidth)/2;
									$("#hp-slideshow .rs-gallery-container .rs-gallery-desc").css("left", offset);
									$(window).resize(function(){
										var getWidth = $(window).width();
