	function eventsByDay(container) {
		//DOC move all events with the same dates into the same container
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();

			};

		});

		$('.ui-widget.app.upcomingevents .ui-widget-detail .ui-articles .upcoming-column.right .ui-article-description').each(function(){
			$(".sw-calendar-block-time", this).before($(".sw-calendar-block-title",this));
		});

		$(".ui-article", container).each(function(){
			var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
			if( newDateTime[2].length == 1 ) newDateTime[2] = "0" + newDateTime[2];
			var newDates = newDateTime[0] + '';
			newDates += newDateTime[1] + '';
			newDates += '' + newDateTime[2] + '';
			console.log(newDates);

			$('.upcoming-column.left h1', this).html(newDates);
		});

		$("div.upcoming-column.left h1.ui-article-title").each(function() {
					var eventDay =	$(".joel-day", this).text()
							.replace("sunday", "Sun /")
							.replace("monday", "Mon /")
							.replace("tuesday", "Tue /")
							.replace("wednesday", "Wed /")
							.replace("thursday", "Thu /")
							.replace("friday", "Fri /")
							.replace("saturday", "Sat /");
					$(".joel-day", this).text(eventDay);	
					console.log(eventDay);
					$(this).prepend($(".joel-day", this));
				});
	}

