		}
	}

	function socialIcons(){
		var socialIcons = [
        		["facebook", "false", "Same Window"],
			["twitter", "false", "Same Window"],
        		["youtube", "false", "Same Window"]
        	];
        	for(var i in socialIcons) {
        		if(socialIcons[i][1] == "true") {
				if(socialIcons[i][2] == "New Window"){
					socialIcons[i][2] == "_blank";
				} else {
					socialIcons[i][2] == "_self";
				}
        			$("#gb-social-icons .icn." + socialIcons[i][0]).removeClass("hidden").attr("target", socialIcons[i][2]);
        		}
        	}
	}
	function footerIcons(){
		var socialIcons = [
			["email", "false"],
        		["powerschool", "false"],
        		["naviance", "false"]
        	];
        	for(var i in socialIcons) {
        		if(socialIcons[i][1] == "false") {
        			$("#gb-social-icons .icn." + socialIcons[i][0]).addClass("hidden");
        		}
        	}
	}
