link.script:
function googleTranslateElementInit() {
    new google.translate.TranslateElement({
        pageLanguage: 'en',
        layout: 0
    }, 'google_translate_element');
}

div.script:
    $(function () {

        $('a[href*=#]:not([href=#])').click(function () {
            if (location.pathname.replace(/^\//, '') == this.pathname.replace(/^\//, '')
                && location.hostname == this.hostname) {

                var target = $(this.hash);
                target = target.length ? target : $('[name=' + this.hash.slice(1) + ']');
                if (target.length) {
                    $('html,body').animate({
                        scrollTop: target.offset().top - 150 //offsets for fixed header
                    }, 1000);
                    return false;
                }
            }
        });
        //Executed on page load with URL containing an anchor tag.
        if ($(location.href.split("#")[1])) {
            var target = $('#' + location.href.split("#")[1]);
            if (target.length) {
                $('html,body').animate({
                    scrollTop: target.offset().top - 150 //offset height of header here too.
                }, 1000);
                return false;
            }
        }

        //Resize function to adjust background image to height
        $('.full-bg-img').css({
            height: $(window).innerHeight()
        });
        $(window).resize(function () { // add resize() to adjust to browser
            $('.full-bg-img').css({
                height: $(window).innerHeight()
            });
        });
    });
    // dropdown menu adjust ased on page width

    $('.dropdown-toggle').on("click", function () {
        var dropdownList = $('.dropdown-menu'),
            dropdownOffset = $(this).offset(),
            offsetLeft = dropdownOffset.left,
            dropdownWidth = dropdownList.width(),
            docWidth = $(window).width(),

            subDropdown = dropdownList.eq(1),
            subDropdownWidth = subDropdown.width(),

            isDropdownVisible = (offsetLeft + dropdownWidth <= docWidth),
            isSubDropdownVisible = (offsetLeft + dropdownWidth + subDropdownWidth <= docWidth);
            if (!isDropdownVisible || !isSubDropdownVisible) {
                dropdownList.addClass('pull-right');
            } else {
                dropdownList.removeClass('pull-right');
            }
            if (!window.matchMedia('(min-width: 992px)').matches) {
                dropdownList.removeClass('pull-right');
            } 
    });

    var _gaq = _gaq || [];
    _gaq.push(['_setAccount', 'UA-18454191-1']);
    _gaq.push(['_trackPageview']);

    (function() {
        var ga = document.createElement('script'); ga.type = 'text/javascript'; ga.async = true;
        ga.src = ('https:' == document.location.protocol ? 'https://ssl' : 'http://www') + '.google-analytics.com/ga.js';
        var s = document.getElementsByTagName('script')[0]; s.parentNode.insertBefore(ga, s);
    })();
