    function csTranslate() {
    	var translateDropdown = '

Translate


';
    	$(translateDropdown).appendTo("#sw-mystart-left");
        $(".sw-mystart-dropdown.translate .sw-dropdown-list").creativeTranslate({
            "type": 2,
            "advancedOptions": {
                "brandedLayout": 1 // 1 = VERTICAL (TEXT UNDER SELECT), 2 = HORIZONTAL (TEXT BESIDE SELECT), 3 = NON-RESPONSIVE MEGA DROPDOWN
            }
        });

        MyStartDropdownActions({
            "dropdownParent": ".sw-mystart-dropdown.translate",
            "dropdownSelector": ".selector",
            "dropdown": ".sw-dropdown",
            "dropdownList": ".sw-dropdown-list"
        });
    }

	function pgScreenSize() {
		//var getWidth = ($("body").hasClass("msie8")) ? screen.width : window.outerWidth;
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery();
			featureRotateTimer();
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery();
			appView();
			if(featureTimerSet == 1){
				clearInterval(window.featureTimer);
				featureTimerSet = 0;
			}
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery();
			appView();
			if(featureTimerSet == 1){
				clearInterval(window.featureTimer);
				featureTimerSet = 0;
			}
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			photoGallery();
			appView();
			appView480();
			if(featureTimerSet == 1){
				clearInterval(window.featureTimer);
				featureTimerSet = 0;
			}
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			photoGallery();
			appView();
			appView480();
			if(featureTimerSet == 1){
				clearInterval(window.featureTimer);
				featureTimerSet = 0;
			}
		}
	}

	function photoGallery() {
		if(!$("#sw-content-container10.region.ui-hp .gallery-container").length){
			// PHOTO GALLERY CONFIGURATION
			$("#sw-content-container10.ui-hp div.ui-widget.app.gallery").creativeGalleryRotate({
				"imageHeight"		: 345,
				"imageControls"		: "on",
				"startStopControls"     : "on", 
				'useSameControlForStartStop' : "on",
				"animateType"		: "slide", // CHOICES - "slide"
				'showSideImages' 	: "on",
				'extraImageSpace'       :  960,
				"animateSpeed"		: 3,
				"pauseHover"		: ['all'], // CHOICES - ['title'], ['overlay'], ['caption'], ['all']
				"autoRotate"		: "on",
				"useTitles"		: "on",
				"useCaptions"		: "on",
				"useOverlay"		: "off",
				"randomOrder"		: "off",
				"linkedElement"		: ['overlay'], // CHOICES - ['picture'], ['title'], ['overlay']
				"itemizedBullets"	: "off",
				"thumbnails"		: "off",
				"thumbnailWidth"	: 50,
				"thumbnailHeight"	: 32,
				"thumbnailControls"	: "off",
				"thumbnailLimit"	: 3,
				"addClass"		: "unclassed",
				"moreLinkText"		: "Read More",
				"rotatingImageColorsList" : ['#30B080','#3060B0','#999999'],
				"rotatingImageColorsElementsList" : ['.desc-bg'] 
			});

			$("#hp-slideshow .image-button.back").html('');
			$("#hp-slideshow .image-button.stop").html('');
			$("#hp-slideshow .image-button.start").html('');
			$("#hp-slideshow .image-button.next").html('');
			$("#hp-slideshow .cs-more-link a").append('');
		}
	}

	function appView() {
		//DOC make homepage apps collapsible for small mobile devices
		if(!$("#hp-content div.ui-widget.app.active").length){
			$("#hp-content div.ui-widget.app:first").addClass("active");
		}
		$("#hp-content div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
		});
	}

	function appView480() {
