	function rotateFeatures() {
		$(".hp-feature-title.active").removeClass("active").next(".hp-feature-title").addClass("active");

		if(!$(".hp-feature-title.active").length){
			$(".hp-feature-title").first().addClass("active");
			$(".hp-feature-slide").removeClass("active");
			$(".hp-feature-slide").first().addClass("active");
		} else {
			$(".hp-feature-slide.active").removeClass("active").next(".hp-feature-slide").addClass("active");
		}
	}

	function featureRotateTimer() {
		var getWidth = $(window).width();
		if($(".hp-feature-title").length > 1 && getWidth >= 1024 && featureTimerSet == 0){
			window.featureTimer = setInterval(function(){
				rotateFeatures();
			}, 8000);
			featureTimerSet = 1;
		}
	}

	function searchText() {
		//DOC custom mystart search placeholder
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "Search");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search");
			}
		});
	}

	function eventsByDay(container) {
		//DOC move all events with the same dates into the same container
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();

			};
		});
		$(".upcomingevents .ui-article-description", container).each(function(){
				$(".upcomingevents .sw-calendar-block-title", this).prependTo($(this));
		});
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
			$("a.ui-btn-toggle.list").click();
			$("a.ui-btn-toggle.month").click();
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
		}

		//var getWidth = ($("body").hasClass("msie8")) ? screen.width : window.outerWidth;
		var getWidth = $(window).width();
		if(getWidth >= 640){ // 640+
			$(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
		} if(getWidth < 640){ // 480-
			$(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
		}
	}

    function MyStartDropdownActions(params) {
        var KeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

        var dropdownParent = params.dropdownParent;
        var dropdownSelector = params.dropdownSelector;
        var dropdown = params.dropdown;
        var dropdownList = params.dropdownList;

        // MYSTART DROPDOWN SELECTOR CLICK EVENT
        //$(document).on("click", dropdownParent + " " + dropdownSelector, function(e) {
        $(dropdownParent + " " + dropdownSelector).click(function(e) {
            e.preventDefault();

            if($(this).parent().hasClass("open")){
                $("+ " + dropdownList + " a").attr("tabindex", "-1");
                $(this).attr({"aria-expanded": "false"}).parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
            } else {
                $(this).attr({"aria-expanded": "true"}).parent().addClass("open").find(dropdown).attr("aria-hidden","false").slideDown(300, "swing");
            }
        });

        // MYSTART DROPDOWN SELECTOR KEYDOWN EVENTS
        $(document).on("keydown", dropdownParent + " " + dropdownSelector, function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case KeyCodes.enter:
                case KeyCodes.space:
                    e.preventDefault();

                    // IF THE DROPDOWN IS OPEN, CLOSE IT
                    if($(dropdownParent).hasClass("open")){
                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $(this).attr({"aria-expanded": "false"}).parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                    } else {
                        $(this).attr({"aria-expanded": "true"}).parent().addClass("open").find(dropdown).attr("aria-hidden", "false").slideDown(300, "swing", function(){
                            $(dropdownList + " li:first-child a", this).attr("tabindex", "0").focus();
                        });
                    }

                break;

                // CONSUME TAB KEY
                case KeyCodes.tab:
                	if($(dropdownParent).hasClass("translate open")) {
                    	$("#cs-branded-translate-label option:first-child").focus();
                    } else {
                      $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                      $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case KeyCodes.down:
                case KeyCodes.right:
                    e.preventDefault();

                    $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                    $("+ " + dropdown + " " + dropdownList + " li:first-child > a", this).attr("tabindex", "0").focus();

                break;
            }
        });

        // MYSTART DROPDOWN LINK KEYDOWN EVENTS
        $(document).on("keydown", dropdownParent + " " + dropdownList + " li a", function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case KeyCodes.left:
                case KeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS DROPDOWN BUTTON
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownParent).find(dropdownSelector).focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case KeyCodes.right:
                case KeyCodes.down:
                    e.preventDefault();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                    }
                break;

                // CONSUME TAB KEY
                case KeyCodes.tab:
                    e.preventDefault();

                    if(e.shiftKey) {
                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                        }
                    } else {
                        // IS LAST ITEM
                        /*if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                        }*/

                        $(dropdownParent).next().find(dropdownSelector).focus();
                    }
                break;

                // CONSUME HOME KEY
                case KeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                    $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                break;

                // CONSUME END KEY
                case KeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                    $(this).closest(dropdownList).find("li:last-child > a").attr("tabindex", "0").focus();
                break;
            }
        });

        $(dropdownParent).mouseleave(function() {
            $("+ " + dropdownList + " a", this).attr("tabindex", "-1");
            $(dropdownSelector, this).attr({"aria-expanded": "false"}).parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
        }).focusout(function() {
        	var thisDropdown = this;
            setTimeout(function () {
                if(!$(thisDropdown).find(":focus").length) {
                    $(dropdownSelector, thisDropdown).attr({"aria-expanded": "false"}).parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                }
            }, 500);
        });


    }

// GET BROWSER AND VERSION
function getBrowser(){
	var N = navigator.appName, ua=navigator.userAgent, tem;
	var M = ua.match(/(opera|chrome|safari|firefox|msie)\/?\s*(\.?\d+(\.\d+)*)/i);
	if(M && (tem= ua.match(/version\/([\.\d]+)/i))!= null) M[2] = tem[1];
	M = M ? [M[1], M[2]] : [N, navigator.appVersion, '-?'];
	return [M[0], M[1]];
}

var browser = getBrowser();
var browserName = browser[0].toLowerCase();
var browserVersion = Math.floor(parseInt(browser[1]));

