        $(document).ready(function () {

            removeBrokenImages();
            checkSidebar();
            RemoveCookie();

            $('div.bullet').attr('tabindex', '');

            $('.navigation li.collapsible').each(function () {
                if ($(this).find('ul').length == 0) {
                    $(this).removeClass('collapsible');
                }
            });

            // find page nav state cookie and add open chevron
            var arrValues = GetCookie('SWPageNavState').split('~');

            $.each(arrValues, function () {
                if (this != '') {
                    $('#' + this).addClass('collapsible').prepend("
");
                }
            });

            // find remaining sub menus and add closed chevron and close menu
            $('.navigation li > ul').each(function () {
                var list = $(this);

                if (list.parent().hasClass('active') && !list.parent().hasClass('collapsible')) {
                    // open sub for currently selected page                    
                    list.parent().addClass('collapsible').prepend("
");
                } else {
                    if (list.parent().hasClass('collapsible') && !list.siblings('div').hasClass('collapsible')) {
                        // open sub for page with auto expand
                        list.siblings('div.expandable').remove();
                        list.parent().prepend("
");
                    }
                }

                if (!list.siblings('div').hasClass('collapsible')) {
                    // keep all closed that aren't already set to open
                    list.parent().addClass('expandable').prepend("
");
                    ClosePageSubMenu(list.parent());
                } else {
                    OpenPageSubMenu(list.parent());
                }
            });

            // remove bullet from hierarchy if no-bullet set
            $('.navigation li.collapsible').each(function () {
                if ($(this).hasClass('no-bullet')) {
                    if (!$(this).hasClass('navigationgroup')) { $(this).removeClass('collapsible'); }
                    $(this).children('div.collapsible').remove();
                }
            });

            $('.navigation li.expandable').each(function () {
                if ($(this).hasClass('no-bullet')) {
                    if (!$(this).hasClass('navigationgroup')) { $(this).removeClass('expandable'); }
                    $(this).children('div.expandable').remove();
                }
            });

            $('.navigation li:not(.collapsible,.expandable,.no-bullet)').each(function () {
                $(this).prepend("
");
            });

            $('.navigation li.active').parents('ul').each(function () {
                if (!$(this).hasClass('page-navigation')) {
                    OpenPageSubMenu($(this).parent());
                }
            });

            // Set aria ttributes
            $('li.collapsible').each(function () {
                $(this).attr("aria-expanded", "true");
                $(this).find('div:first').attr('aria-pressed', 'true');
            });

            $('li.expandable').each(function () {
                $(this).attr("aria-expanded", "false");
                $(this).find('div:first').attr('aria-pressed', 'false');
            });

            $('div.bullet').each(function () {
                $(this).attr("aria-hidden", "true");
            });

            // set click event for chevron
            $(document).on('click', '.navigation div.collapsible', function () {
                ClosePageSubMenu($(this).parent());
            });

            $(document).on('click', '.navigation div.expandable', function () {
                OpenPageSubMenu($(this).parent());
            });

            // set navigation grouping links
            $(document).on('click', '.navigationgroup.collapsible > a', function () {
                ClosePageSubMenu($(this).parent());
            });

            $(document).on('click', '.navigationgroup.expandable > a', function () {
                OpenPageSubMenu($(this).parent());
            });

            //SW MYSTART DROPDOWNS
            $(document).on('click', '.sw-mystart-dropdown', function () {
                $(this).children(".sw-dropdown").css("display", "block");
            });

            $(".sw-mystart-dropdown").hover(function () { }, function () {
                $(this).children(".sw-dropdown").hide();
                $(this).blur();
            });

            //SW ACCOUNT DROPDOWN
            $(document).on('click', '#sw-mystart-account', function () {
                $(this).children("#sw-myaccount-list").show();
                $(this).addClass("clicked-state");
            });

            $("#sw-mystart-account, #sw-myaccount-list").hover(function () { }, function () {
                $(this).children("#sw-myaccount-list").hide();
                $(this).removeClass("clicked-state");
                $("#sw-myaccount").blur();
            });

            // set hover class for page and section navigation
            $('.ui-widget.app.pagenavigation, .ui-widget.app.sectionnavigation').find('li > a').hover(function () {
                $(this).addClass('hover');
            }, function () {
                $(this).removeClass('hover');
            });

            //set aria-label for home
            $('#navc-HP > a').attr('aria-label', 'Home');

            // set active class on channel and section
            var activeChannelNavType = $('input#hidActiveChannelNavType').val();
            if (activeChannelNavType == -1) {
                // homepage is active
                $('#navc-HP').addClass('active');
            } else if (activeChannelNavType == 1) {
                // calendar page is active
                $('#navc-CA').addClass('active');
            } else {
                // channel is active - set the active class on the channel
                var activeSelectorID = $('input#hidActiveChannel').val();
                $('#navc-' + activeSelectorID).addClass('active');

                // set the breadcrumb channel href to the channel nav href
                $('li[data-bccID=' + activeSelectorID + '] a').attr('href', $('#navc-' + activeSelectorID + ' a').attr('href'));
                $('li[data-bccID=' + activeSelectorID + '] a span').text($('#navc-' + activeSelectorID + ' a span').first().text());

                // set the active class on the section
                activeSelectorID = $('input#hidActiveSection').val();
                $('#navs-' + activeSelectorID).addClass('active');

                // set the breadcrumb section href to the channel nav href
                $('li[data-bcsID=' + activeSelectorID + '] a').attr('href', $('#navs-' + activeSelectorID + ' a').attr('href'));
                if ($('#navs-' + activeSelectorID + ' a').attr('target') !== undefined) {
                    $('li[data-bcsID=' + activeSelectorID + '] a').attr('target', $('#navs-' + activeSelectorID + ' a').attr('target'));
                }
                $('li[data-bcsID=' + activeSelectorID + '] span').text($('#navs-' + activeSelectorID + ' a span').text());

                if ($('.sw-directory-columns').length > 0) {
                    $('ul.ui-breadcrumbs li:last-child').remove();
                    $('ul.ui-breadcrumbs li:last-child a').replaceWith(function() { return $('span', this); });
                    $('ul.ui-breadcrumbs li:last-child span').append(' Directory');
                }
            }
        }); // end document ready

        function OpenPageSubMenu(li) {
            if (li.prop('tagName').toLowerCase() == "li") {
                if (li.hasClass('expandable')) {
                    li.removeClass('expandable').addClass('collapsible');
                }
                if (li.find('div:first').hasClass('expandable')) {
                    li.find('div:first').removeClass('expandable').addClass('collapsible').attr('aria-pressed', 'true').attr('aria-label','Close Page Submenu');
                }
                li.find('ul:first').attr('aria-hidden', 'false').show();

                li.attr("aria-expanded", "true");

                PageNavigationStateCookie();
            }
        }

        function ClosePageSubMenu(li) {
            if (li.prop('tagName').toLowerCase() == "li") {
                li.removeClass('collapsible').addClass('expandable');
                li.find('div:first').removeClass('collapsible').addClass('expandable').attr('aria-pressed', 'false').attr('aria-label','Open Page Submenu');
                li.find('ul:first').attr('aria-hidden', 'true').hide();

                li.attr("aria-expanded", "false");

                PageNavigationStateCookie();
            }
        }

        function PageNavigationStateCookie() {
            var strCookie = "";

            $('.pagenavigation li > ul').each(function () {
                var item = $(this).parent('li');
                if (item.hasClass('collapsible') && !item.hasClass('no-bullet')) {
                    strCookie += $(this).parent().attr('id') + '~';
                }
            });

            SetCookie('SWPageNavState', strCookie);
        }

        function checkSidebar() {
            $(".ui-widget-sidebar").each(function () {
                if ($.trim($(this).html()) != "") {
                    $(this).show();
                    $(this).siblings(".ui-widget-detail").addClass("with-sidebar");
                }
            });
        }

        function removeBrokenImages() {
            //REMOVES ANY BROKEN IMAGES
            $("span.img img").each(function () {
                if ($(this).attr("src") !== undefined && $(this).attr("src") != '../../') {
                    $(this).parent().parent().show();
                    $(this).parent().parent().siblings().addClass("has-thumb");
                }
            });
        }

        function LoadEventDetailUE(moduleInstanceID, eventDateID, userRegID, isEdit) {
            (userRegID === undefined ? userRegID = 0 : '');
            (isEdit === undefined ? isEdit = false : '');
            OpenDialogOverlay("WindowMediumModal", { LoadType: "U", LoadURL: "https://www.gardencity.k12.ny.us//site/UserControls/Calendar/EventDetailWrapper.aspx?ModuleInstanceID=" + moduleInstanceID + "&EventDateID=" + eventDateID + "&UserRegID=" + userRegID + "&IsEdit=" + isEdit });
        }

        function RemoveCookie() {
            // There are no sub page            
            if ($('.pagenavigation li li').length == 0) {
                //return false;
                PageNavigationStateCookie();
            }
        }

        function AddOffCanvasMenuHeightForSiteNav() {
            var sitenavulHeight = 0;

            if ($('#sw-pg-sitenav-ul').length > 0) {
                sitenavulHeight = parseInt($("#sw-pg-sitenav-ul").height());
            }

            var swinnerwrapHeight = 0;

            if ($('#sw-inner-wrap').length > 0) {
                swinnerwrapHeight = parseInt($("#sw-inner-wrap").height());
            }

            // 360px is abount 5 li height
            if (sitenavulHeight + 360 >= swinnerwrapHeight) {
                $("#sw-inner-wrap").height(sitenavulHeight + 360);
            }
        }

        function AddOffCanvasMenuHeightForSelectSchool() {
            var selectschoolulHeight = 0;

            if ($('#sw-pg-selectschool-ul').length > 0) {
                selectschoolulHeight = parseInt($("#sw-pg-selectschool-ul").height());
            }

            var swinnerwrapHeight = 0;

            if ($('#sw-inner-wrap').length > 0) {
                swinnerwrapHeight = parseInt($("#sw-inner-wrap").height());
            }

            // 360px is abount 5 li height
            if (selectschoolulHeight + 360 >= swinnerwrapHeight) {
                $("#sw-inner-wrap").height(selectschoolulHeight + 360);
            }
        }

        $(document).ready(function () {
            if ($("#sw-pg-sitenav-a").length > 0) {
                $(document).on('click', '#sw-pg-sitenav-a', function () {
                    if ($("#sw-pg-sitenav-ul").hasClass('sw-pgmenu-closed')) {
                        AddOffCanvasMenuHeightForSiteNav();

                        $("ul.sw-pgmenu-toplevel").removeClass('sw-pgmenu-open').addClass('sw-pgmenu-closed');
                        $("#sw-pg-sitenav-ul").removeClass('sw-pgmenu-closed');
                        $("#sw-pg-sitenav-ul").addClass('sw-pgmenu-open');
                    } else {
                        $("#sw-pg-sitenav-ul").removeClass('sw-pgmenu-open');
                        $("#sw-pg-sitenav-ul").addClass('sw-pgmenu-closed');
                    }
                });

                $(document).on('click', '#sw-pg-selectschool-a', function () {
                    if ($("#sw-pg-selectschool-ul").hasClass('sw-pgmenu-closed')) {
                        AddOffCanvasMenuHeightForSelectSchool();

                        $("ul.sw-pgmenu-toplevel").removeClass('sw-pgmenu-open').addClass('sw-pgmenu-closed');
                        $("#sw-pg-selectschool-ul").removeClass('sw-pgmenu-closed');
                        $("#sw-pg-selectschool-ul").addClass('sw-pgmenu-open');
                    } else {
                        $("#sw-pg-selectschool-ul").removeClass('sw-pgmenu-open');
                        $("#sw-pg-selectschool-ul").addClass('sw-pgmenu-closed');
                    }
                });

                $(document).on('click', '#sw-pg-myaccount-a', function () {
                    if ($("#sw-pg-myaccount-ul").hasClass('sw-pgmenu-closed')) {
                        $("ul.sw-pgmenu-toplevel").removeClass('sw-pgmenu-open').addClass('sw-pgmenu-closed');
                        $("#sw-pg-myaccount-ul").removeClass('sw-pgmenu-closed');
                        $("#sw-pg-myaccount-ul").addClass('sw-pgmenu-open');
                    } else {
                        $("#sw-pg-myaccount-ul").removeClass('sw-pgmenu-open');
                        $("#sw-pg-myaccount-ul").addClass('sw-pgmenu-closed');
                    }
                });

                $(document).on('click', '.pg-list-bullet', function () {
                    $(this).prev().toggle();

                    if ($(this).hasClass('closed')) {
                        AddOffCanvasMenuHeightForSiteNav();

                        $(this).removeClass('closed');
                        $(this).addClass('open');
                    } else {
                        $(this).removeClass('open');
                        $(this).addClass('closed');
                    }
                });

                $(document).on('mouseover', '#sw-pg-selectschool', function () {
                    $("#sw-pg-selectschool-firstli").removeClass('sw-pg-selectschool-firstli-mouseout').addClass('sw-pg-selectschool-firstli-mouseover');
                    $("#sw-pg-selectschool-firstli a").addClass('sw-pg-selectschool-firstli-a-mouseover').removeClass('sw-pg-selectschool-firstli-a-mouseout');
                });

                $(document).on('mouseout', '#sw-pg-selectschool', function () {
                    $("#sw-pg-selectschool-firstli").removeClass('sw-pg-selectschool-firstli-mouseover').addClass('sw-pg-selectschool-firstli-mouseout');
                    $("#sw-pg-selectschool-firstli a").addClass('sw-pg-selectschool-firstli-a-mouseout').removeClass('sw-pg-selectschool-firstli-a-mouseover');
                });

                $(document).on('mouseover', '#sw-pg-myaccount', function () {
                    $("#sw-pg-myaccount-firstli").removeClass('sw-pg-myaccount-firstli-mouseout').addClass('sw-pg-myaccount-firstli-mouseover');
                    $("#sw-pg-myaccount-firstli a").addClass('sw-pg-myaccount-firstli-a-mouseover').removeClass('sw-pg-myaccount-firstli-a-mouseout');
                });

                $(document).on('mouseout', '#sw-pg-myaccount', function () {
                    $("#sw-pg-myaccount-firstli").removeClass('sw-pg-myaccount-firstli-mouseover').addClass('sw-pg-myaccount-firstli-mouseout');
                    $("#sw-pg-myaccount-firstli a").addClass('sw-pg-myaccount-firstli-a-mouseout').removeClass('sw-pg-myaccount-firstli-a-mouseover');
                });

                $(document).on('mouseover', '#sw-pg-sitenav', function () {
                    $("#sw-pg-sitenav-firstli").removeClass('sw-pg-sitenav-firstli-mouseout').addClass('sw-pg-sitenav-firstli-mouseover');
                    $("#sw-pg-sitenav-firstli a").addClass('sw-pg-sitenav-firstli-a-mouseover').removeClass('sw-pg-sitenav-firstli-a-mouseout');
                });

                $(document).on('mouseout', '#sw-pg-sitenav', function () {
                    $("#sw-pg-sitenav-firstli").removeClass('sw-pg-sitenav-firstli-mouseover').addClass('sw-pg-sitenav-firstli-mouseout');
                    $("#sw-pg-sitenav-firstli a").addClass('sw-pg-sitenav-firstli-a-mouseout').removeClass('sw-pg-sitenav-firstli-a-mouseover');
                });

                $(document).on('mouseover', '#sw-pg-district', function () {
                    $("#sw-pg-district-firstli").removeClass('sw-pg-district-firstli-mouseout').addClass('sw-pg-district-firstli-mouseover');
                    $("#sw-pg-district-firstli a").addClass('sw-pg-district-firstli-a-mouseover').removeClass('sw-pg-district-firstli-a-mouseout');
                });

                $(document).on('mouseout', '#sw-pg-district', function () {
                    $("#sw-pg-district-firstli").removeClass('sw-pg-district-firstli-mouseover').addClass('sw-pg-district-firstli-mouseout');
                    $("#sw-pg-district-firstli a").addClass('sw-pg-district-firstli-a-mouseout').removeClass('sw-pg-district-firstli-a-mouseover');
                });
            }
        });

            $(document).ready(function () {
                $.smartbanner({
                    title: 'Garden City Union Free School District', // Title of the app - CHANGE FOR BRANDED APP ONLY
                    author: 'Schoolwires, Inc', // Author Name Blackboard, Inc.
                    price: 'FREE', // Price
                    appStoreLanguage: 'us', // Language code for App Store
                    inAppStore: 'On the App Store', // Text after price for iOS
                    inGooglePlay: 'In Google Play', // Text after price for Android
                    inAmazonAppStore: 'In the Amazon Appstore',
                    inWindowsStore: 'In the Windows Store', //Text of price for Windows
                    GooglePlayParams: null, // Aditional parameters for the market
                    icon: 'GlobalAssets/Images/MobileNative/sw_app_logo.png', // The URL of the icon - SET THIS TO THE iOS APP STORE ICON 
			    iconGloss: false, // Force gloss effect
			    button: 'VIEW', // Text for the install button
			    url: null, // The URL for the button. Keep null if you want the button to link to the app store.
			    scale: 'auto', // Scale based on viewport size (set to 1 to disable)
			    speedIn: 300, // Show animation speed of the banner
			    speedOut: 400, // Close animation speed of the banner
			    daysHidden: 1, // Duration to hide the banner after being closed (0 = always show banner)
			    daysReminder: 30, // Duration to hide the banner after "VIEW" is clicked *separate from when the close button is clicked* (0 = always show banner)
			    force: null, // Choose 'ios' or 'android'. Don't do a browser check, just always show this banner
			    hideOnInstall: true, // Hide the banner after "VIEW" is clicked.
			    layer: false, // Display as overlay layer or slide down the page
			    iOSUniversalApp: true, // If the iOS App is a universal app for both iPad and iPhone, display Smart Banner to iPad users, too.
			    appendToSelector: 'body', //Append the banner to a specific selector
			    pushSelector: 'html' // What element is going to push the site content down; this is where the banner append animation will start.
                });
            });

        // DO NOT EDIT BELOW THIS LINE

        /*!
 * jQuery Smart Banner
 * Copyright (c) 2012 Arnold Daniels 
 * Based on 'jQuery Smart Web App Banner' by Kurt Zenisek @ kzeni.com
 */
        !function ($) {
            var SmartBanner = function (options) {
                this.origHtmlMargin = parseFloat($('html').css('margin-top')) // Get the original margin-top of the HTML element so we can take that into account
                this.options = $.extend({}, $.smartbanner.defaults, options)

                var standalone = navigator.standalone // Check if it's already a standalone web app or running within a webui view of an app (not mobile safari)
                  , UA = navigator.userAgent

                // Detect banner type (iOS or Android)
                if (this.options.force) {
                    this.type = this.options.force
                } else if (UA.match(/Windows Phone 8/i) != null && UA.match(/Touch/i) !== null) {
                    this.type = 'windows'
                } else if (UA.match(/iPhone|iPod/i) != null || (UA.match(/iPad/) && this.options.iOSUniversalApp)) {
                    if (UA.match(/Safari/i) != null &&
                       (UA.match(/CriOS/i) != null ||
                       window.Number(UA.substr(UA.indexOf('OS ') + 3, 3).replace('_', '.')) < 6)) this.type = 'ios' // Check webview and native smart banner support (iOS 6+)
                } else if (UA.match(/\bSilk\/(.*\bMobile Safari\b)?/) || UA.match(/\bKF\w/) || UA.match('Kindle Fire')) {
                    this.type = 'kindle'
                } else if (UA.match(/Android/i) != null) {
                    this.type = 'android'
                }

                // Don't show banner if device isn't iOS or Android, website is loaded in app or user dismissed banner
                if (!this.type || standalone || this.getCookie('sb-closed') || this.getCookie('sb-installed')) {
                    return
                }

                // Calculate scale
                this.scale = this.options.scale == 'auto' ? $(window).width() / window.screen.width : this.options.scale
                if (this.scale < 1) this.scale = 1

                // Get info from meta data
                var meta = $(this.type == 'android' ? 'meta[name="google-play-app"]' :
                    this.type == 'ios' ? 'meta[name="apple-itunes-app"]' :
                    this.type == 'kindle' ? 'meta[name="kindle-fire-app"]' : 'meta[name="msApplication-ID"]');
                if (meta.length == 0) return

                // For Windows Store apps, get the PackageFamilyName for protocol launch
                if (this.type == 'windows') {
                    this.appId = $('meta[name="msApplication-PackageFamilyName"]').attr('content');
                } else {
                    // Try to pull the appId out of the meta tag and store the result
                    var parsedMetaContent = /app-id=([^\s,]+)/.exec(meta.attr('content'));

                    if (parsedMetaContent) {
                        this.appId = parsedMetaContent[1];
                    } else {
                        return;
                    }
                }

                this.title = this.options.title ? this.options.title : meta.data('title') || $('title').text().replace(/\s*[|\-·].*$/, '')
                this.author = this.options.author ? this.options.author : meta.data('author') || ($('meta[name="author"]').length ? $('meta[name="author"]').attr('content') : window.location.hostname)
                this.iconUrl = meta.data('icon-url');
                this.price = meta.data('price');

                // Create banner
                this.create()
                this.show()
                this.listen()
            }

            SmartBanner.prototype = {

                constructor: SmartBanner

              , create: function () {
                  var iconURL
                    , link = (this.options.url ? this.options.url : (this.type == 'windows' ? 'ms-windows-store:navigate?appid=' : (this.type == 'android' ? 'market://details?id=' : (this.type == 'kindle' ? 'amzn://apps/android?asin=' : 'https://itunes.apple.com/' + this.options.appStoreLanguage + '/app/id'))) + this.appId)
                    , price = this.price || this.options.price
                    , inStore = price ? price + ' - ' + (this.type == 'android' ? this.options.inGooglePlay : this.type == 'kindle' ? this.options.inAmazonAppStore : this.type == 'ios' ? this.options.inAppStore : this.options.inWindowsStore) : ''
                    , gloss = this.options.iconGloss === null ? (this.type == 'ios') : this.options.iconGloss

                  if (this.type == 'android' && this.options.GooglePlayParams) {
                      link = link + '&referrer=' + this.options.GooglePlayParams;
                  }

                  var banner = '

×
' + this.title + '' + this.author + '' + inStore + '' + this.options.button + '';
                  (this.options.layer) ? $(this.options.appendToSelector).append(banner) : $(this.options.appendToSelector).prepend(banner);

                  if (this.options.icon) {
                      iconURL = this.options.icon
                  } else if (this.iconUrl) {
                      iconURL = this.iconUrl;
                  } else if ($('link[rel="apple-touch-icon-precomposed"]').length > 0) {
                      iconURL = $('link[rel="apple-touch-icon-precomposed"]').attr('href')
                      if (this.options.iconGloss === null) gloss = false
                  } else if ($('link[rel="apple-touch-icon"]').length > 0) {
                      iconURL = $('link[rel="apple-touch-icon"]').attr('href')
                  } else if ($('meta[name="msApplication-TileImage"]').length > 0) {
                      iconURL = $('meta[name="msApplication-TileImage"]').attr('content')
                  } else if ($('meta[name="msapplication-TileImage"]').length > 0) { /* redundant because ms docs show two case usages */
                      iconURL = $('meta[name="msapplication-TileImage"]').attr('content')
                  }

                  if (iconURL) {
                      $('#smartbanner .sb-icon').css('background-image', 'url(' + iconURL + ')')
                      if (gloss) $('#smartbanner .sb-icon').addClass('gloss')
                  } else {
                      $('#smartbanner').addClass('no-icon')
                  }

                  this.bannerHeight = $('#smartbanner').outerHeight() + 2

                  if (this.scale > 1) {
                      $('#smartbanner')
                          .css('top', parseFloat($('#smartbanner').css('top')) * this.scale)
                          .css('height', parseFloat($('#smartbanner').css('height')) * this.scale)
                          .hide()
                      $('#smartbanner .sb-container')
                          .css('-webkit-transform', 'scale(' + this.scale + ')')
                          .css('-msie-transform', 'scale(' + this.scale + ')')
                          .css('-moz-transform', 'scale(' + this.scale + ')')
                          .css('width', $(window).width() / this.scale)
                  }
                  if(this.options.layer==false){
                      $('#smartbanner').css('position', 'static');
                      if (this.type == 'android') {
                          $('#smartbanner').css({'position':'relative','top':'0px','left':'0px'});
                      }
                  }else{
                      $('#smartbanner').css('position', 'absolute');
                  }
              }

              , listen: function () {
                  $('#smartbanner .sb-close').on('click', $.proxy(this.close, this))
                  $('#smartbanner .sb-button').on('click', $.proxy(this.install, this))
              }

              , show: function (callback) {
                  var banner = $('#smartbanner');
                  banner.stop();

                  if (this.options.layer) {
                      banner.animate({ top: 0, display: 'block' }, this.options.speedIn).addClass('shown').show();
                      $(this.pushSelector).animate({ paddingTop: this.origHtmlMargin + (this.bannerHeight * this.scale) }, this.options.speedIn, 'swing', callback);
                  } else {
                      if ($.support.transition) {
                          banner.animate({ top: 0 }, this.options.speedIn).addClass('shown');
                          var transitionCallback = function () {
                              $('html').removeClass('sb-animation');
                              if (callback) {
                                  callback();
                              }
                          };
                          $(this.pushSelector).addClass('sb-animation').one($.support.transition.end, transitionCallback).emulateTransitionEnd(this.options.speedIn).css('margin-top', this.origHtmlMargin + (this.bannerHeight * this.scale));
                      } else {
                          banner.slideDown(this.options.speedIn).addClass('shown');
                      }
                  }
              }

              , hide: function (callback) {
                  var banner = $('#smartbanner');
                  banner.stop();

                  if (this.options.layer) {
                      banner.animate({ top: -1 * this.bannerHeight * this.scale, display: 'block' }, this.options.speedIn).removeClass('shown');
                      $(this.pushSelector).animate({ paddingTop: this.origHtmlMargin }, this.options.speedIn, 'swing', callback);
                  } else {
                      if ($.support.transition) {
                          if (this.type !== 'android')
                              banner.css('top', -1 * this.bannerHeight * this.scale).removeClass('shown');
                          else
                              banner.css({ display: 'none' }).removeClass('shown');
                          var transitionCallback = function () {
                              $('html').removeClass('sb-animation');
                              if (callback) {
                                  callback();
                              }
                          };
                          $(this.pushSelector).addClass('sb-animation').one($.support.transition.end, transitionCallback).emulateTransitionEnd(this.options.speedOut).css('margin-top', this.origHtmlMargin);
                      } else {
                          banner.slideUp(this.options.speedOut).removeClass('shown');
                      }
                  }
              }

              , close: function (e) {
                  e.preventDefault()
                  this.hide()
                  this.setCookie('sb-closed', 'true', this.options.daysHidden);
              }

              , install: function (e) {
                  if (this.options.hideOnInstall) {
                      this.hide()
                  }
                  this.setCookie('sb-installed', 'true', this.options.daysReminder)
              }

              , setCookie: function (name, value, exdays) {
                  var exdate = new Date()
                  exdate.setDate(exdate.getDate() + exdays)
                  value = encodeURI(value) + ((exdays == null) ? '' : '; expires=' + exdate.toUTCString())
                  document.cookie = name + '=' + value + '; path=/;'
              }

              , getCookie: function (name) {
                  var i, x, y, ARRcookies = document.cookie.split(";")
                  for (i = 0; i < ARRcookies.length; i++) {
                      x = ARRcookies[i].substr(0, ARRcookies[i].indexOf("="))
                      y = ARRcookies[i].substr(ARRcookies[i].indexOf("=") + 1)
                      x = x.replace(/^\s+|\s+$/g, "")
                      if (x == name) {
                          return decodeURI(y)
                      }
                  }
                  return null
              }

                // Demo only
              , switchType: function () {
                  var that = this

                  this.hide(function () {
                      that.type = that.type == 'android' ? 'ios' : 'android'
                      var meta = $(that.type == 'android' ? 'meta[name="google-play-app"]' : 'meta[name="apple-itunes-app"]').attr('content')
                      that.appId = /app-id=([^\s,]+)/.exec(meta)[1]

                      $('#smartbanner').detach()
                      that.create()
                      that.show()
                  })
              }
            }

            $.smartbanner = function (option) {
                var $window = $(window)
                , data = $window.data('smartbanner')
                , options = typeof option == 'object' && option
                if (!data) $window.data('smartbanner', (data = new SmartBanner(options)))
                if (typeof option == 'string') data[option]()
            }

            // override these globally if you like (they are all optional)
            $.smartbanner.defaults = {
                title: null, // What the title of the app should be in the banner (defaults to )
                author: null, // What the author of the app should be in the banner (defaults to  or hostname)
                price: 'FREE', // Price of the app
                appStoreLanguage: 'us', // Language code for App Store
                inAppStore: 'On the App Store', // Text of price for iOS
                inGooglePlay: 'In Google Play', // Text of price for Android
                inAmazonAppStore: 'In the Amazon Appstore',
                inWindowsStore: 'In the Windows Store', //Text of price for Windows
                GooglePlayParams: null, // Aditional parameters for the market
                icon: null, // The URL of the icon (defaults to )
                iconGloss: null, // Force gloss effect for iOS even for precomposed
                button: 'VIEW', // Text for the install button
                url: null, // The URL for the button. Keep null if you want the button to link to the app store.
                scale: 'auto', // Scale based on viewport size (set to 1 to disable)
                speedIn: 300, // Show animation speed of the banner
                speedOut: 400, // Close animation speed of the banner
                daysHidden: 15, // Duration to hide the banner after being closed (0 = always show banner)
                daysReminder: 90, // Duration to hide the banner after "VIEW" is clicked *separate from when the close button is clicked* (0 = always show banner)
                force: null, // Choose 'ios', 'android' or 'windows'. Don't do a browser check, just always show this banner
                hideOnInstall: true, // Hide the banner after "VIEW" is clicked.
                layer: false, // Display as overlay layer or slide down the page
                iOSUniversalApp: true, // If the iOS App is a universal app for both iPad and iPhone, display Smart Banner to iPad users, too.
                appendToSelector: 'body', //Append the banner to a specific selector
                pushSelector: 'html' // What element is going to push the site content down; this is where the banner append animation will start.
            }

            $.smartbanner.Constructor = SmartBanner;


            // ============================================================
            // Bootstrap transition
            // Copyright 2011-2014 Twitter, Inc.
            // Licensed under MIT (https://github.com/twbs/bootstrap/blob/master/LICENSE)

            function transitionEnd() {
                var el = document.createElement('smartbanner')

                var transEndEventNames = {
                    WebkitTransition: 'webkitTransitionEnd',
                    MozTransition: 'transitionend',
                    OTransition: 'oTransitionEnd otransitionend',
                    transition: 'transitionend'
                }

                for (var name in transEndEventNames) {
                    if (el.style[name] !== undefined) {
                        return { end: transEndEventNames[name] }
                    }
                }

                return false // explicit for ie8 (  ._.)
            }

            if ($.support.transition !== undefined)
                return  // Prevent conflict with Twitter Bootstrap

            // http://blog.alexmaccaw.com/css-transitions
            $.fn.emulateTransitionEnd = function (duration) {
                var called = false, $el = this
                $(this).one($.support.transition.end, function () {
                    called = true
                })
                var callback = function () {
                    if (!called) $($el).trigger($.support.transition.end)
                }
                setTimeout(callback, duration)
                return this
            }

            $(function () {
                $.support.transition = transitionEnd()
            })
            // ============================================================

        }(window.jQuery);
